insert  into `sys_config`(`config_id`,`config_name`,`config_key`,`config_value`,`config_type`,`create_by`,`create_time`,`update_by`,`update_time`,`remark`) values (1,'主框架页-默认皮肤样式名称','sys.index.skinName','skin-blue','Y','admin','2021-12-03 18:11:45','',NULL,'蓝色 skin-blue、绿色 skin-green、紫色 skin-purple、红色 skin-red、黄色 skin-yellow'),(2,'用户管理-账号初始密码','sys.user.initPassword','123456','Y','admin','2021-12-03 18:11:46','',NULL,'初始化密码 123456'),(3,'主框架页-侧边栏主题','sys.index.sideTheme','theme-dark','Y','admin','2021-12-03 18:11:46','',NULL,'深色主题theme-dark，浅色主题theme-light'),(4,'账号自助-是否开启用户注册功能','sys.account.registerUser','true','Y','admin','2021-12-03 18:11:46','admin','2022-04-16 17:48:54','是否开启注册用户功能（true开启，false关闭）');

/*Table structure for table `sys_dept` */

DROP TABLE IF EXISTS `sys_dept`;

CREATE TABLE `sys_dept` (
  `dept_id` bigint NOT NULL AUTO_INCREMENT COMMENT '部门id',
  `parent_id` bigint DEFAULT '0' COMMENT '父部门id',
  `ancestors` varchar(50) DEFAULT '' COMMENT '祖级列表',
  `dept_name` varchar(30) DEFAULT '' COMMENT '部门名称',
  `order_num` int DEFAULT '0' COMMENT '显示顺序',
  `leader` varchar(20) DEFAULT NULL COMMENT '负责人',
  `phone` varchar(11) DEFAULT NULL COMMENT '联系电话',
  `email` varchar(50) DEFAULT NULL COMMENT '邮箱',
  `status` char(1) DEFAULT '0' COMMENT '部门状态（0正常 1停用）',
  `del_flag` char(1) DEFAULT '0' COMMENT '删除标志（0代表存在 2代表删除）',
  `create_by` varchar(64) DEFAULT '' COMMENT '创建者',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_by` varchar(64) DEFAULT '' COMMENT '更新者',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `describe` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '' COMMENT '科室描述',
  PRIMARY KEY (`dept_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1002 DEFAULT CHARSET=utf8 COMMENT='部门表';

/*Data for the table `sys_dept` */

insert  into `sys_dept`(`dept_id`,`parent_id`,`ancestors`,`dept_name`,`order_num`,`leader`,`phone`,`email`,`status`,`del_flag`,`create_by`,`create_time`,`update_by`,`update_time`,`describe`) values (100,0,'0','普渡健康医院',0,'万一','15888888888','ry@qq.com','0','0','admin','2021-12-03 18:09:54','admin','2022-03-06 15:11:25',''),(215,100,'0,100','行政管理部门',3,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 19:58:48','admin','2022-03-30 20:04:05',''),(216,100,'0,100','医技部门',2,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 19:59:23','',NULL,''),(217,100,'0,100','临床部门',1,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:00:52','admin','2022-03-30 20:03:50',''),(218,100,'0,100','医疗辅助部门',4,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:02:36','',NULL,''),(219,100,'0,100','后勤保障部门',5,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:03:37','',NULL,''),(220,217,'0,100,217','骨科',1,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:04:49','',NULL,'普渡健康医院骨科，创立于20世纪20年代，是我国一流的骨科学临床和研究中心，骨科注重年轻骨干力量的培养，不仅让他们学习、继承老专家的优良品质和工作作风，还先后安排多位年轻大夫去澳大利亚、日本、美国、法国、香港、台湾等国家和地区进修、深造，目前都已学成归国，成为科室的骨干。骨科同时还注重与国际上的著名院校和专家进行交流，曾先后邀请美国、法国、日本、韩国、英国等国的骨科专家来院进行手术演示、作学术报告、研讨问题，以提高骨科的诊治和科研水平；注重科研和医疗的结合，先后申请并承担了北京市科委、国家自然基金重点、面上项目及国家863课题首席科学家等科研项目86项，累计科研经费逾4380万元人民币。'),(221,217,'0,100,217','眼科',2,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:06:26','admin','2022-03-30 20:09:23','普渡健康医院眼科成立于1920年，是我国最早成立的眼科。成立后许多国际一流的眼科学家曾在这里主持工作，被誉为“东方的维也纳眼科中心”。经过90年的发展，协和眼科成为专业齐全，融医、教、研为一体的眼科基地，整体水平在国内处于领先地位，一些项目达国际先进水平。协和眼科是国内首批硕士、博士学位培养点和博士后流动站，首批住院医师规范化培训基地。'),(222,217,'0,100,217','胃肠外科',4,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:09:15','',NULL,'普渡健康医院胃肠外科是教育部国家重点学科、国家卫计委临床重点专科、我国胃肠外科疑难疾病诊治中心、卫生部内镜专业技术普通外科培训基地。中心长期致力于胃肠外科创新手术的探索与实践，旨在用最小的创伤、最好的手术效果为胃肠外科患者服务。中心现有床位156张（加床180张），年手术量6600余台。'),(223,217,'0,100,217','泌尿外科',3,'万一·','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:05:54','admin','2022-03-30 20:09:29','普渡健康医院泌尿外科始建于二十世纪四十年代。我科作为卫生部临床重点专科，每年主办多次全国会议。先后获得卫生部科技进步奖1项，中华医学科技奖1项，华夏医学奖1项，院校科研与医疗成果奖20多项，与其他科室合作获得的国家科技进步奖一项。有国家自然科学基金等多项研究课题。重视学术水平，鼓励进行科研设计和发表学术论文，每年发表国内核心期刊文章及SCI文章三十余篇。获国家发明专利5项。'),(224,217,'0,100,217','消化内科',6,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:07:12','admin','2022-03-30 20:09:47','消化内科是普渡健康医院成立最早的专科之一，由我国现代内科学泰斗罗宾教授和著名消化病专家乌索普教授亲自领导和培育起来。经历了几代人的艰辛努力，消化内科已发展成为在国内外均很有影响的重点学科。消化内科的亚专业齐全、人才济济、临床和实验室技术实力雄厚，是一个高水平的消化病学、内镜学以及疑难重症的诊治中心。科内设有高水平的、面向全国的炎症性肠病和胰腺疾病诊治中心、消化内镜培训中心以及胃肠功能和动力疾病诊治中心。'),(225,217,'0,100,217','心脏内科',5,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:08:06','',NULL,'普渡健康医院心脏内科由大蛇丸教授创立，经过几十年艰苦创业，逐渐发展成为国家临床重点专科、西部地区疑难危重心血管病诊疗中心、四川省心血管疾病质量控制中心，是四川省内唯一由国家卫计委评定的冠心病、心律失常和先心病介入诊疗培训基地。 '),(226,216,'0,100,216','放射科',1,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:10:28','',NULL,''),(227,216,'0,100,216','输血科',2,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:10:58','',NULL,''),(228,216,'0,100,216','药剂科',3,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:11:53','',NULL,''),(229,215,'0,100,215','院办公室',1,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:12:49','admin','2022-04-03 16:40:44',''),(230,215,'0,100,215','医务科',2,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:13:15','',NULL,''),(231,215,'0,100,215','财务科',3,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:13:57','admin','2022-04-03 16:41:29',''),(232,218,'0,100,218','门诊中心',1,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:14:33','admin','2022-03-30 20:20:26',''),(233,218,'0,100,218','咨询中心',2,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:19:21','',NULL,''),(234,218,'0,100,218','住院中心',3,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:21:24','',NULL,''),(235,219,'0,100,219','食堂',1,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:22:15','',NULL,''),(236,219,'0,100,219','洗衣房',2,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:22:47','',NULL,''),(237,218,'0,100,218','计算机中心',4,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:23:32','',NULL,''),(238,219,'0,100,219','车队',3,'万一','13037277783','123456@qq.com','0','0','admin','2022-03-30 20:24:02','',NULL,''),(239,215,'0,100,215','人事科',4,'万一','13037277783','123456@qq.com','0','0','admin','2022-04-03 16:41:21','',NULL,''),(1001,100,'0,100','尚未分配部门',6,'万一','13037277783','506579631@qq.com','0','0','admin','2022-05-02 15:29:12','',NULL,'');

/*Table structure for table `sys_dict_data` */

DROP TABLE IF EXISTS `sys_dict_data`;

