/*Table structure for table `sys_notice` */

DROP TABLE IF EXISTS `sys_notice`;

CREATE TABLE `sys_notice` (
  `notice_id` int NOT NULL AUTO_INCREMENT COMMENT '公告ID',
  `notice_title` varchar(50) NOT NULL COMMENT '公告标题',
  `notice_type` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '公告类型（1科普 2公告,3医院历史）',
  `notice_content` longblob COMMENT '公告内容',
  `status` char(1) DEFAULT '0' COMMENT '公告状态（0正常 1关闭）',
  `create_by` varchar(64) DEFAULT '' COMMENT '创建者',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_by` varchar(64) DEFAULT '' COMMENT '更新者',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  `image_url` varchar(64) DEFAULT '' COMMENT '图片链接',
  PRIMARY KEY (`notice_id`)
) ENGINE=InnoDB AUTO_INCREMENT=69 DEFAULT CHARSET=utf8 COMMENT='通知公告表';

/*Data for the table `sys_notice` */

insert  into `sys_notice`(`notice_id`,`notice_title`,`notice_type`,`notice_content`,`status`,`create_by`,`create_time`,`update_by`,`update_time`,`remark`,`image_url`) values (10,'普渡健康医院信息网络计算机等设备询价公告','2','<p>普渡健康医院将对下列内容进行公开询价，具体需求参数及资质要求等信息详见附件。欢迎符合条件的供应商参与报价。</p><p>1、保卫处安装壁挂式一体机询价函</p><p>2、超融合集群smartx网络冗余改造需求询价函</p><p>	报价文件需盖章扫描发送至指定邮箱。</p><p>	联系人：&nbsp;左老师&nbsp;020-62787073</p>','0','admin','2022-04-04 21:38:40','admin','2022-04-15 22:39:13','管理员',''),(11,'关于开展2022 年度科技成果登记工作的通知','2','<p>    各科室：</p><p>	2021年度成果登记工作已开始，现将有关事项通知如下：</p><p>	一、登记范围</p><p>	山东省内单位研究开发，或作为第一完成单位与省外合作研究开发产生的科技成果（包括基础理论成果、应用技术成果和软科学成果等）其中：承担各级、各类科技计划，由省内科技管理部门主持或受上级科技管理部门委托组织验收或结题的科技项目产生的科技成果必须登记。</p><p>	二、登记流程</p><p>	1.成果登记申报人登陆国家科技成果信息网（http://www.tech110.net/）下载安装“国家科技成果登记系统（V10.0）”，填写完整后导出电子版文件。</p><p>	2.将电子登记系统整理导出的电子版压缩包、项目汇总表发送至邮箱：tyfykyk@163.com。有转化需求的项目或者重大项目需填写科技成果转化表（附件2）和重大科技成果推荐表（附件3），以电子版格式一并报送。</p><p>	三、填写要求</p><p>	1.成果概况、立项情况必须如实详细填写，不得有漏填项。</p><p>	2.评价情况、知识产权情况、成果转移转化情况、成果转化需求要求如实填写，如确实没有可以不用填写。</p><p>	3.成果完成单位情况、成果完成人员名单、评价委员会名单、成果简介要求必须填写详细完整。基金类项目（包括国家基金和省基金）可以不用填写评价委员会名单。</p><p>	四、截止时间</p><p>	请务必于2021年12月8日前完成全部工作，并将有关材料报送至科研科。</p><p>	附件：1：重大科技成果推荐表；2：公示函格式；3：科技成果信息表（有转化需求）；4：成果评价委托说明</p>','0','admin','2022-04-04 21:39:31','admin','2022-04-15 22:43:15','管理员',''),(12,'普渡健康医院多酶清洗剂采购项目废标公告','2','<p>	一、项目基本情况</p><p>	采购项目编号：SDTX-2021-073</p><p>	采购项目名称：普渡健康医院多酶清洗剂采购项目</p><p>	二、项目废标的原因</p><p>	实质性响应磋商文件的供应商不足三家，作废标处理。</p><p>	三、其他补充事宜：</p><p>	无</p><p>	四、凡对本次公告内容提出询问，请按以下方式联系。</p><p>	1.采购人信息</p><p>	名称：普渡健康医院</p><p>	地址：泰安市泰山区泰山大街366号</p><p>	联系方式：0538-6236467</p><p>	2.采购代理机构信息</p><p>	名 称：山东同信建设项目管理有限公司</p><p>	地 址：泰安市泰山大街326号金华大厦六楼608室</p><p>	联系方式：0538-8221207</p><p>	3.项目联系方式</p><p>	项目联系人：刘明云</p><p>	电 话：0538-8221207</p>','0','admin','2022-04-04 21:40:02','admin','2022-04-15 22:45:17','管理员',''),(13,'医疗废物处置项目单一来源谈判公告','2','<p>	一、采购人名称：普渡健康医院</p><p>	二、采购项目名称：普渡健康医院医疗废物处置项目</p><p>	三、采购项目内容：按照医疗废物处置相关规定，对我院医疗废物进行运输并集中处置，服务期1年。</p><p>	四、公示内容：</p><p>	本项目为普渡健康医院医疗废物处置项目，单一来源采购项目，拟由泰安市泰阳环保服务有限公司提供处置服务，现将项目予以公示。</p><p>	五、谈判日期：2021年10月8日</p><p>	谈判地点：后勤管理部办公室</p><p>	质疑电话：0538-6236439</p>','0','admin','2022-04-04 21:40:30','admin','2022-04-15 22:47:45','管理员',''),(14,'护理岗位聘用制人员招聘笔试通知','2','<p>	根据医院工作安排和疫情防控工作需要，现将护理岗位2021年聘用制人员招聘笔试具体安排通知如下：</p><p>	一、笔试安排</p><p>	1、笔试时间：2021年9月27日（周一）：下午2:00至3:00</p><p>	2、笔试地点：普渡大学天外村校区（迎胜东路2号）</p><p>	3、考试方式：实行闭卷考试形式，总分为100分，笔试内容为应聘岗位所需专业基础知识。</p><p>	4、携带材料：身份证、资格审核合格单、考试所需文具。</p><p>	5、按照1:3的比例确定进入面试人员名单（参加考试人员不足1:3的岗位，按照实际参加考试人员进入面试范围）。</p><p>	注：面试时间另行通知。</p><p>	二、其他要求</p><p>	1、参加招聘人员务必于笔试当天下午12:20之前到达考试地点。考试开始后仍未到达现场的考生视为自动放弃，取消考试资格。</p><p>	2、根据疫情防控要求，请参加考试人员务必携带以下材料：48小时内核酸检测结果报告、健康码、近14天行程、疫苗接种记录等资料,佩戴一次性医用外科口罩,严格做好个人防护。</p><p>	</p><p>	特别提醒：请绿色出行，校内无法停车。</p>','0','admin','2022-04-04 21:41:05','admin','2022-04-15 22:48:47','管理员',''),(15,'高层次人才就医绿色通道公示','2','<p>	持有江西惠才卡及泰山金卡的高层次人才来我院就诊，可享受绿色通道服务，现将服务专员及预约相关电话公示如下：</p><p>	1.单位名称：普渡健康医院</p><p>	2.负责科室：干部保健与查体中心</p><p>	3.联系电话：0538-6236372</p><p>	4.服务专员：冉张申</p><p>	5.联系电话：185 0538 7089</p><p>	6.绿色通道电话：0538-6236901</p><p>	7.服务预约电话：185 0538 7089</p><p>	微信预约公众号名称：普渡健康医院</p><p>	预约网址: http://www.keyi.world</p>','0','admin','2022-04-04 21:41:29','admin','2022-04-15 22:50:19','管理员',''),(16,'2021年聘用制人员招聘笔试、面试通知','2','<p>	根据医院工作安排和疫情防控工作需要，2021年聘用制人员招聘工作分批次进行，现将第一批次岗位招聘具体时间安排通知如下：</p><p>	一、岗位</p><p>	药剂科、医学影像科、康复医学科、病理科、检验科</p><p>	二、笔试</p><p>	1、笔试时间：2021年9月17日（周五）：上午8:30至9:30</p><p>	2、笔试地点：医院综合楼四楼大会议室</p><p>	3、考试方式：实行闭卷考试形式，总分为100分，笔试内容为应聘岗位所需专业基础知识。</p><p>	4、按照1:3的比例确定进入面试人员名单（参加考试人员不足1:3的岗位，按照实际参加考试人员进入面试范围）。</p><p>	三、面试</p><p>	1、面试时间：2021年9月17日（周五）上午10：30。</p><p>	2、面试地点：医院综合楼四楼小会议室</p><p>	3、面试考察：总分为100分，主要考察理论素养、思维能力、创新能力以及与所报岗位相适应的专业知识、业务能力等方面进行考核。</p><p>	注：面试成绩设最低分数线，低于60分者不能进入体检范围。</p><p>	四、确定进入体检范围人员名单</p><p>	按照总成绩（笔试、面试各占50%）高低确定体检范围人员名单。</p><p>	五、其他要求</p><p>	1、参加招聘人员请于笔试、面试当天上午7:30之前到达考试地点。考试开始后仍未到达现场的考生视为自动放弃，取消考试资格。</p><p>	2、根据疫情防控要求，请资格审核人员务必携带以下材料：48小时内核酸检测结果报告、健康码、近14天行程、疫苗接种记录等资料,佩戴一次性医用外科口罩,严格做好个人防护。</p>','0','admin','2022-04-04 21:41:55','admin','2022-04-15 22:51:40','管理员',''),(17,'人才招聘公告','2','<p>	<span style=\"color: rgb(0, 0, 0);\">根据疫情防控要求，拟定于本周五（8月6日）下午组织麻醉科1、医院感染管理科、人事管理三个岗位进行笔试、面试，其余岗位视疫情防控情况分批组织笔试、面试。详情请咨询0538-6236412。</span></p>','0','admin','2022-04-04 21:42:19','admin','2022-04-15 22:52:51','管理员',''),(18,'关于申报2021年度山东省自然科学基金创新发展联合基金项目的通知','2','<p>	各科室：</p><p>	按照《山东省自然科学基金项目管理办法》《山东省自然科学基金创新发展联合基金项目管理办法》和山东省自然科学基金创新发展联合基金（以下简称：联合基金）相关协议规定，现就联合基金项目申报有关事项通知如下。</p><p>	<strong>一、实施依据</strong></p><p>	联合基金是山东省自然科学基金的组成部分，其申报、评审、管理和资金使用等按照《山东省自然科学基金项目管理办法》《山东省自然科学基金创新发展联合基金项目管理办法》《山东省自然科学基金项目资助经费管理办法》以及相关协议执行。</p><p>	<strong>二、资助领域</strong></p><p>	本次联合基金项目资助领域主要包括智慧计算、氟硅材料、量子科学、肿瘤防治、生物医药和中医药领域。</p><p>	<strong>三、申报条件</strong></p><p>	1. 项目申报人为主体研究思路的提出者和实际主持研究的科技人员，在相关领域具有较高学术水平，具有相关的研究经历和研发能力，拥有较强组织协调管理能力和工作责任心。</p><p>	2. 项目申报人有2项（含）以上山东省科技计划在研项目的不得申报；已申报2021年度省自然科学基金项目（第一批）及重大基础研究项目的不得申报，申报人同年只能申报1项联合基金项目。申报期内申报人有未结题省自然科学基金项目（2021年7月1日前项目结题报告已经推荐单位审核提交的除外）的不得申报。</p><p>	3. 根据《山东省自然科学基金项目管理办法》《山东省自然科学基金创新发展联合基金项目管理办法》相关规定，不具备申报省自然科学基金条件的不得申报。连续2次申报省自然科学基金项目未获立项的，不允许申报。</p><p>	4. 面向全国发布的智慧计算、氟硅材料、生物医药和中医药领域，省外单位申报需联合省内注册单位作为依托单位进行申报。合作单位不超过2家，重点支持项目有合作单位的需提供合作协议。</p><p>	<strong>四、申报方式及时间</strong></p><p>	1. 项目申报通过山东省科技云平台（http://cloud.sdstc.gov.cn/）在线填报，使用省政府统一服务门户注册的个人账号进行网上申报，审核推荐程序按照《山东省自然科学基金管理办法》有关规定执行。</p><p>	2. 项目申报人网上填报提交时间为2021年8月2日9:00至2021年8月16日17:00，逾期填报无效。</p><p>	3. 依托单位及推荐部门审核提交截止时间为2021年8月18日17:00，逾期审核提交无效。</p><p>	请项目申报人合理安排项目申报时间，避免临近系统关闭时集中上传或审核。</p><p>	业务咨询电话：0531-66777204；技术支持电话：0531-66777094</p>','0','admin','2022-04-04 21:42:46','admin','2022-04-15 22:54:15','管理员',''),(19,'关于申报山东省老年医学学会科技攻关计划项目的通知','2','<p>	各科室：</p><p>	现就2021年度山东省老年医学学会科技攻关计划项目申报有关事宜通知如下：</p><p>	一、申报说明</p><p>	1、项目研究方向符合山东省老年医学学会规划要求。本次重点支持老年病临床医学领域关键技术研究。</p><p>	2、项目负责人在同一年度只能申报1项科技攻关计划项目。</p><p>	3、申报项目分重点项目、一般项目和无资项目三类。前两类项目给予经费资助，无资项目经费自筹。</p><p>	二、项目申报及项目申请人要求</p><p>	1、申报项目负责人必须为山东省老年医学学会会员。</p><p>	2、项目负责人应具有中级及以上专业技术职称；或具有硕士及以上学位，有相关课题研究基础和经验。</p><p>	3、申报项目必须经单位同意，临床研究项目要经伦理委员会审查通过后方可报送，项目申请书要求规范按时报送。</p><p>	4、申请人所在单位需具有相应的科研项目管理和伦理审查制度，财务账户能接收课题研究经费。</p><p>	三、申报方式及时间</p><p>	申报者请下载《山东省老年医学学会科技攻关计划项目申报书》，并如实填写申请书，纸质版一式两份交至科研科,电子版发送至tyfykyk@163.com。申截止时间2021年8月23日，逾期不予受理。</p><p>	四、注意事项</p><p>	1、项目实施周期一般为2年，项目须按期验收，确保实现预期目标，并按照规定要求提交科技报告。</p><p>	2、根据2021年申报情况，经费资助强度在0-5万元/项。项目立项后，批复经费低于申请经费的项目，原则上由申请单位或协作单位自筹或配套。</p><p><br></p>','0','admin','2022-04-04 21:43:16','admin','2022-04-15 22:54:49','管理员',''),(28,'关于耳鸣，你必须知道的几个真相','1','<p>	<span style=\"color: rgb(112, 112, 112);\">耳鸣在耳鼻喉科是常见病、多发病，而且属于比较难治的疾病。长期耳鸣会影响人们日常的人际交往、工作生活等各个方面，容易导致焦虑、抑郁等不良情绪的产生，严重者可能发展为精神心理疾病。</span></p><p class=\"ql-align-center\">		<strong>什么是耳鸣？</strong></p><p>	耳鸣是指当外界没有声音时，我们却能听到声音的现象。耳鸣声特征多样，可以是蝉鸣声、流水声、轰鸣声、电流声等。耳鸣在普通人群中的发病率约为10~20%，在老年人中发病率更高。</p><p class=\"ql-align-center\">	<strong>为什么会耳鸣？</strong></p><p>	耳鸣和很多因素相关，包括年龄、听力下降、睡眠、应激反应等。</p><p>	很多耳鸣患者同时存在听力下降的情况。目前，耳鸣的发病机制尚未完全明确，但主流学说认为，耳鸣和中枢神经系统可塑性改变相关。</p><p>	我们大脑中的电信号分为兴奋性电信号和抑制性电信号。在正常情况下，两种信号处于相对平衡的状态。在听力下降的情况下，外界传入的听觉信息（兴奋性信号）减少，大脑为了达到新的稳态，会打破原来的平衡，整体向相对兴奋的状态偏移，也就导致了在外界没有听觉信号传入的情况下，我们却能听到兴奋性信号产生的声音。</p><p class=\"ql-align-center\">	<strong>出现耳鸣=患有严重疾病？</strong></p><p>	不一定。</p><p>	耳鸣可能和很多种疾病相伴出现，也可能单独产生。最常伴随耳鸣出现的是耳部疾病，包括突发性耳聋、梅尼埃病、听神经瘤等。一些血管源性病变，包括颈静脉球高位、乙状窦憩室等解剖结构异常等，也可以导致耳鸣。有些耳鸣患者伴有认知及记忆障碍、焦虑、抑郁等情绪问题，若出现这些情况，建议及时就诊。</p><p class=\"ql-align-center\">	<strong>出现耳鸣怎么办？</strong></p><p>	如果是一过性耳鸣（不伴有眩晕、耳闷等其他不适，很快可以缓解），同时发生在情绪激动、劳累、睡眠不佳等诱发因素之后，首先应该去除这些诱发因素，即调整作息，保证充足的睡眠。</p><p>	若改善这些因素后，耳鸣仍不能缓解，建议前往医院就诊。根据耳鸣的性质、侧别、持续时间、诱发因素等，医生会建议完善听力测试、颅脑核磁共振等检查。若能发现明显异常，如耵聍栓塞、鼓膜穿孔、传导性听力下降等，医生会结合患者病情给出具体的治疗方案。</p><p>	若所有检查都没有明显异常，可排除肿瘤等器质性病变，此时可确定为感音神经性耳鸣。患者应放松心情，根据医生的推荐，尝试以下治疗方案。</p><p>	<strong>1、耳鸣声治疗</strong></p><p>	前面提到，耳鸣与听力下降导致的神经系统重塑有关。针对听力下降的程度和耳鸣的特点，医生会制定个性化的音频片段，患者定期规律地听这些声音，有可能改善耳鸣。</p><p>	目前已有部分研究证明，耳鸣声治疗的有效率约为70%。北京协和医院耳鼻喉科目前在开展耳鸣声治疗项目，有耳鸣困扰的患者可前往咨询。</p><p>	</p><p>	<strong>2、耳鸣认知行为疗法</strong></p><p>	耳鸣的发病率相对较高，但仅有少数患者感到耳鸣明显影响了生活质量，需要借助医疗手段进行干预。认知行为疗法是通过告知耳鸣患者一些关于耳鸣的知识，使其不再焦虑、恐惧。有证据表明，该治疗可明显降低耳鸣对患者生活的不利影响。需要注意的是，该方法需要专业的治疗团队对患者进行心理干预，因耳鸣严重影响生活质量的患者可以尝试。</p><p>	目前耳鸣领域的有关研究人员正在探索其他治疗方法，包括经颅核刺激、双相刺激、电刺激、行为认知疗法，习服疗法等，患者也可根据自身的情况选择尝试。</p><p>	关于耳鸣的上述几个真相，你学会了吗？</p>','0','admin','2022-04-05 19:49:55','',NULL,NULL,'kepu-1.jpg'),(29,'世界睡眠日｜春眠不觉晓，夜来呼噜知多少？','1','<p>	<span style=\"color: rgb(112, 112, 112);\">3月21日是世界睡眠日，今年的主题是“良好睡眠 健康同行”。众所周知，睡眠紊乱不仅可以导致睡眠质量差，白天疲乏和嗜睡，还与许多疾病相关，如高血压、糖尿病、脑卒中等。而导致睡眠紊乱最常见的“幕后黑手”，就是阻塞性睡眠呼吸暂停（OSA），我们常说的打呼噜，也就是打鼾，是阻塞性睡眠呼吸暂停最突出的表现。</span></p><p class=\"ql-align-center\">		<strong>打鼾的人都有睡眠呼吸暂停吗？</strong></p><p>	大多数阻塞性睡眠呼吸暂停的病人都是由于打鼾来就诊的，但是并不是每个打鼾的人都有睡眠呼吸暂停。</p><p>	打鼾是睡眠中非常常见的现象，大约40%的成年男性和24%的成年女性是习惯性打鼾者，很多人觉得这是“睡得香”。其实不然，轻度打鼾的人都没有什么感觉，仅仅觉得早上起来疲乏，精力不好。但是需要特别注意的是，如果晚上鼾声很大，且不规律，影响家人休息，反复出现睡眠中呼吸停止，夜间有窒息感或憋醒，睡眠质量差，醒来后仍然感到疲倦或精神不振，很难集中注意力，甚至是出现控制不住的睡意，这可能是由于患了严重的阻塞性睡眠呼吸暂停。</p><p>	阻塞性睡眠呼吸暂停还可能出现晚上失眠、夜尿增多，早上头痛、头晕、咽痛，记忆下降、情绪改变以及性功能下降。</p><p>	划重点：平卧位、饮酒、镇静安眠药、感冒或者过敏性鼻炎均可能导致或者加重打鼾。</p><p class=\"ql-align-center\">	<strong>阻塞性睡眠呼吸暂停有哪些危害？</strong></p><p>	目前普遍认为阻塞性睡眠呼吸暂停是一种全身性慢性疾病，不仅影响我们的生活质量，降低工作效率，还是许多心脑血管疾病、糖尿病的危险因素，同时又是引起猝死、道路交通事故的重要因素。</p><p>	<strong>主要危害</strong></p><p>	心肌梗死、急性冠脉综合征和脑卒中的发生率明显增高</p><p>	高血压的风险增加了1倍</p><p>	房颤的风险增加了55%</p><p>	发生心源性猝死的风险增高，大约为一般人群和无OSA个体的3倍</p><p>	增加脑卒中风险1～5倍，呼吸暂停越重，风险越高</p><p>	糖尿病的风险增加一半</p><p>	阿尔茨海默病的发病风险增加一半</p><p>	车祸风险增加2～3倍</p><p class=\"ql-align-center\">	<strong>哪些人容易患阻塞性睡眠呼吸暂停？</strong></p><p>	超重：超重或肥胖(体重指数为30或以上)</p><p>	颈粗：颈围40cm以上</p><p>	中青年</p><p>	男性</p><p>	高血压</p><p>	镇静药物</p><p>	气道异常：例如鼻中隔偏曲或鼻息肉</p><p>	颌面部异常：小下颌、下颌后缩等</p><p>	有睡眠呼吸暂停家族史的人</p><p class=\"ql-align-center\">	<strong>如何诊断阻塞性睡眠呼吸暂停？</strong></p><p>	<strong>多导睡眠图：</strong>目前诊断阻塞性睡眠呼吸暂停的标准方法，也就是在睡眠中心过夜，睡觉时身体连接各种传感器，记录脑电波、心跳、呼吸和动作，从而来评估睡眠中的睡眠和呼吸情况。但是这种方式往往需要排队等候床位，而且经常由于换床而睡眠差。</p><p>	<strong>家庭睡眠呼吸监测：</strong>可以携带小型监测仪器返回自己家做监测收集数据，第二天下载数据进行分析。这种方式比较简单，但是由于采集的数据较少，有时候会低估结果或者误诊，只适合一部分病人。</p><p>	小贴士：应用哪种设备需要和医生沟通再进行选择。</p><p class=\"ql-align-center\">	<strong>如何治疗阻塞性睡眠呼吸暂停?</strong></p><p>	阻塞性睡眠呼吸暂停是一种需要治疗的严重睡眠障碍。目前仍没有明确有效的药物治疗，主要依靠改变生活方式，包括戒烟戒酒、侧卧位睡眠、减体重等。</p><p>	对于中重度的阻塞性睡眠呼吸暂停的首选治疗是应用家用无创呼吸机治疗，少数的人可选择口腔矫正器或者手术治疗。</p><p>	如果自己或者家人打鼾，且鼾声很大、鼾声不规律，或者出现白天不明原因的疲倦、精力差和嗜睡，应该注意有无阻塞性睡眠呼吸暂停，及时就诊，让身体得到最佳的休息，从而改善健康状况！</p>','0','admin','2022-04-05 19:52:16','',NULL,NULL,'kepu-2.jpg'),(30,'抽血后胳膊一片青仿佛被毒打，多半是你没压对','1','<p>	<span style=\"color: rgb(112, 112, 112);\">相信您一定有过在这样的抽血经历——抽血后，您是否在穿刺点周围一片淤青，甚至胀疼，又红又紫好几天都消不下去？回想一下，您是否刚抽完血，就把棉签一摁，手肘一屈，衣服一披，直接走人，边走边不时伸直手肘，看看血止住了没，没有就再重新夹紧，看上它几个回合，最后觉得应该是止血了，棉签一扔完事？相信不少人肯定“中招”了！事实上，抽血后出现皮肤淤青，多是由于拔针后没有及时、有效按压，导致静脉血管出血至皮下引起的。避免瘀青，好的按压手法和过硬的采血技术一样重要哦！</span></p><p class=\"ql-align-center\">		<strong>为什么按压不当会出现皮肤瘀青？</strong></p><p>	<strong>1、压迫时间短</strong></p><p>	有些人因急于离开采血区域，或携带的随身物品较多，因此在抽血后，往往只按压几秒钟，看到表面不出血就不再按压。实际上，皮下血管上的针眼仍在出血，为了防止皮下出血应按压至少5分钟，而凝血功能不全或者口服阿司匹林类药物的患者应适当再延长按压时间。</p><p>	<strong>2、压迫位置不当或面积过小</strong></p><p>	采血时，针头刺入皮肤后会滑行一段距离再刺入血管。拔针后，很多人会只压迫皮肤穿刺点，而忽略了对主要出血点——血管壁穿刺点的压迫，导致血管穿刺点在深部组织出血，引起皮下淤血、皮肤淤青。</p><p>	<strong>3、按压力度不当</strong></p><p>	按压力度过轻导致穿刺点压迫不完全，就不能达到有效止血效果。按压力度过重，会引起局部毛细血管破裂而产生瘀血，而且如果过于用力，则在撤走棉签等止血物品时，受瞬间较大的血流冲击力影响，可能再次出血。</p><p>	<strong>4、边按边揉</strong></p><p>	临床上常有许多患者在拨针后用棉签边按边揉，他们认为，这样可以减轻局部疼痛感。实际上，这样不利于穿刺点止血，而且经过穿刺后的血管及皮下毛细血管非常脆弱，即使轻微的揉按也会造成局部瘀血。</p><p class=\"ql-align-center\">	<strong>正确按压手法学起来</strong></p><p>	<strong>1、压迫面积要足够</strong></p><p>	采血后穿刺点的按压面积要足够，方法有大鱼际按压法、拇指握压法、三指按压法。</p><p>	无论哪种方法，要领就是按压面积要大，以保证皮肤穿刺点和血管穿刺点能同时被按压到。</p><p>	<strong>2、只按不揉，持续按压，力度均匀适中</strong></p><p>	如上所述，轻揉穿刺点不仅不能止血，还会加快出血，效果适得其反；按压需持续、用力均匀，时间至少达到5分钟。如果按一会儿看一下，不仅影响止血速度，且更容易导致淤青。</p><p>	<strong>3、肘部应该伸直</strong></p><p>	屈肘按压会增加额外压力，并使皮肤穿刺点与血管上穿刺点相对位置发生变化，按压点随皮肤移动而远离穿刺点，血管穿刺点因未能有效受压而发生皮下渗血。</p><p class=\"ql-align-center\">	<strong>如果已经出现瘀青怎么办？</strong></p><p>	<strong>1、24小时内冷敷</strong></p><p>	如果已经出现淤青，24小时内可以用冷毛巾或冰袋冷敷，使血管收缩，加快止血。避免该侧肢体提拎重物。</p><p>	<strong>2、24小时后热敷</strong></p><p>	在淤青出现24小时后，再用热毛巾热敷，促进血液循环，加快化淤速度，也可涂抹一些活血化淤的外用药。</p><p>	<strong>3、及时就医</strong></p><p>	一般情况下，穿刺点周边出现小面积淤青，三至七天即可复原，但如果渗血量过多，造成淤青面积较大或局部血肿，应及时就医处理。</p><p>	看到这里，你学会正确的按压手法了吗？下次抽完血记得好好按压哟。</p>','0','admin','2022-04-05 19:54:05','',NULL,NULL,'kepu-3.jpg'),(31,'世界防治结核病日｜可累及各个器官，当心中招“浪漫病”！','1','<p>	<span style=\"color: rgb(112, 112, 112);\">3月24日（明天）是世界防治结核病日，而今年正好是罗伯特·科赫先生发现结核分枝杆菌140周年。今年的宣传主题是Invest to End TB. Save Lives（生命至上 全力投入 终结结核），终结结核病离不开社会各界的共同努力。为什么结核病又名“浪漫病”？结核病就是肺结核吗？肺结核就一定会传染吗？</span></p><p class=\"ql-align-center\">		<strong>\"浪漫病\"的前世今生</strong></p><p>	结核病是一种古老的疾病，最早可以追溯至7万年前，曾经被称为“白色瘟疫”。历史上至少有2亿人因为结核失去了生命。但在18~19世纪的欧洲，结核病被称为\"浪漫病\"，因患病者均体形消瘦、皮肤苍白、面色潮红，这就是大量文学作品在这一时期非常追捧的“玫瑰香腮”，美人失血的嘴唇与嘴角的血迹，以及因疾病折磨而流露出的忧郁气质，这种苍白艳丽的美，曾风靡欧洲一时。</p><p>	然而掀开浪漫主义的面纱后，我们更多看到的却是潮红的脸颊，神经质的气质，弱不经风的体格和漫长的治疗过程。</p><p>	终于，在1882年，人类文明终于迎来了划时代的发现：德国微生物学家罗伯特·科赫发现了导致结核病的元凶——结核分枝杆菌。为纪念这一重大发现，1995年底，世界卫生组织将每年的3月24日作为世界防治结核病日，而今年正是罗伯特·科赫先生发现结核分枝杆菌的140周年。</p><p class=\"ql-align-center\">	<strong>结核病=肺结核？</strong></p><p>	结核病是由结核分枝杆菌感染人体所致，几乎可以累及各个器官，表现多种多样，人称”万能模仿者”。</p><p>	结核病中最高发的是肺结核，通过呼吸道传播。</p><p>	所以结核病≠肺结核。除肺结核外，肺外结核也非常多见，人体除头发、牙齿和指甲外的任何组织和器官都可能受到结核分枝杆菌的侵犯。以北京协和医院这类综合医院为例，医院并未设有专门收治结核病患者的病房和专病门诊，但是每天却有很多需要确诊或排除结核病的患者，由于肺外结核病取得病原学诊断困难，需要结合临床、检验、影像、病理等综合诊断。</p><p class=\"ql-align-center\">	<strong>“元凶”（结核分枝杆菌）为啥这么难消灭？</strong></p><p>	结核分枝杆菌在与人类共同进化的过程中，为适应生存环境，变得越来越“聪明”。</p><p>	其传染性强、十分”狡猾”(懂得长期潜伏、伺机而动)，同时有耐药性(可对多种一线抗结核治疗药物耐药)、致病复杂性(复治患者耐药率约18%)、难获得病原(我国肺结核患者中病原学确诊率约55%，综合医院活动性结核病患者病原学确诊占比不到1/3）等等buff加持，形成了独特的致病特征。</p><p class=\"ql-align-center\">	<strong>“结核”=一定会传染？</strong></p><p>	结核病的传染源是正在患病的活动性肺结核患者，其中病原学阳性的肺结核患者是主要的传染源。</p><p>	当肺结核患者咳嗽、打喷嚏、大声说话时，可产生大量含结核菌的飞沫微粒，通过空气、灰尘传播，进入健康的小伙伴体内。而结核分枝杆菌潜伏感染者、治愈的肺结核患者不具有传染性。</p><p>	<strong>结核分枝杆菌潜伏感染者</strong></p><p>	结核分枝杆菌感染人体后，大部分人可依靠自身免疫力将结核菌清除，或是和结核菌达到“暂时性和解”，即潜伏性结核感染(人体受到结核分枝杆菌抗原刺激后出现的持续性免疫反应，但未显现出活动性结核病临床证据的一种状态)。据估计，全球25%的人口存在潜伏性结核感染，他们一生中有5%~10%的风险发展为活动性结核病，大多数(约70%左右)发生在潜伏性结核感染后最初的两年内。</p><p class=\"ql-align-center\">	<strong>结核病，哪些人容易中招？</strong></p><p>	感染者是否发病不仅与感染结核菌的数量、毒性因素有关，还与人体免疫力有关。如婴幼儿、老年人、营养不良、尘肺、糖尿病患者、恶性肿瘤患者、肾脏病患者、HIV感染者、器官移植者、长期应用激素抑制剂者、生活饮食不规律及精神压力大的潜伏性结核感染者发生活动性结核病的风险远大于免疫健全的人。在我国开展的ETHERTB调查研究结果显示，风湿病患者活动性结核患病率几乎是普通人群患病率的两倍。</p><p class=\"ql-align-center\">	<strong>怀疑自己得了结核病，怎么办？</strong></p><p>	如果近期有新出现的活动性结核症状，如发热、咳嗽、咳痰、盗汗、体重下降等，有些人没有任何症状，只通过健康查体发现胸片惊现“阴影”，就开始胡乱猜测，是肿瘤？结核？</p><p>	肺部阴影不一定是肺癌，主要分为感染性疾病和非感染性疾病。感染性疾病包括结核、其他细菌、真菌、病毒等病原体感染。而非感染性疾病则包括肺癌、肺受累等等。</p><p>	出现上述症状，建议及时到正规医院感染科或呼吸科就诊。首先进行胸部CT检查、验血、结核菌素皮肤试验、痰液检查等。即使确诊也不必太过担心，结核病一般预后良好，部分结核病患者可通过自身抵抗力自愈，肺部影像表现为钙化灶，绝大多数患者只要遵医嘱，经过正规的抗结核治疗就可以痊愈。但切忌隐瞒病情或盲目就医而导致延误治疗、传染他人。</p><p class=\"ql-align-center\">	<strong>预防结核，我们可以做什么？</strong></p><p>	对于每个普通人而言，我们可以在饮食与运动方面来增强自身免疫力，规律作息，保持乐观心态，尽量避免与肺结核患者接触，若探视需戴好N95口罩，注意室内通风，多吃蔬果、多晒太阳、戒烟戒酒，一旦出现疑似结核症状（发热、咳嗽、咳痰、盗汗、体重下降等）及时就医。</p>','0','admin','2022-04-05 19:55:30','',NULL,NULL,'kepu-4.jpg'),(32,'孩子磕磕碰碰不用怕，三招教您家庭自理常见小外伤','1','<p>	<span style=\"color: rgb(112, 112, 112);\">小孩子爱跑跳爱运动，安全意识差，常常因磕磕碰碰出现小外伤，让家长们担心不已。发生外伤后，您是否了解消毒产品应该怎么用，怎样避免伤口感染……？</span></p><p class=\"ql-align-center\">		<strong>紫药水、红药水是什么</strong></p><p>	紫药水是按照10克甲基紫兑1升水的比例制成的。甲基紫也常被称作龙胆紫，刺激性较小。</p><p>	</p><p>	紫药水的缺点：</p><p>	一、杀菌能力较差。</p><p>	二、收敛性较强，易使创面结硬痂，影响渗出物的排出，同时使痂下细菌滋生，反而会加重病情。</p><p>	三、因为颜色较深，涂抹后不利于观察伤口的情况。</p><p>	四、动物研究发现，紫药水有潜在致癌风险。</p><p>	目前，紫药水只在手术中作为标识剂使用。</p><p>	</p><p>	</p><p>	红药水又名汞溴红，是含有重金属汞的有机化合物。</p><p>	红药水的缺点：</p><p>	当伤口太大或太深时使用红药水，过多的汞离子会经皮肤进入体内，引起中毒。</p><p>	同紫药水一样，红药水的颜色也会影响对伤口愈合情况的判断。值得强调的是，碘酒和红药水不能一起使用。因为碘酒的主要成分是碘，碘和汞溴红混合在一起会析出有剧毒的碘化汞。</p><p class=\"ql-align-center\">	<strong>常见外伤如何处理？</strong></p><p>	既然紫药水、红药水因各有缺点而逐步退出市场，那么如今市面上的消毒产品谁更强？</p><p>	双氧水、酒精、碘伏……这些外伤消毒药品是“各有所长”还是“殊途同归”？在准备常用药品时，需要尽数备齐还是择一即可？常见的外伤应该如何处理呢？</p><p>	</p><p>	<strong>擦伤、割伤</strong></p><p>	严重指数：★☆☆☆☆</p><p>	基本特点：</p><p>	擦伤只是表皮受伤，深度浅、面积小，伤势一般比较轻微，可以不用包扎。</p><p>	一般处理：</p><p>	1、先用清水或肥皂水进行冲洗和清理，如果伤口或伤口周边有污物，最好用生理盐水冲洗。</p><p>	2、用碘伏涂抹伤口周围的皮肤。</p><p>	3、涂抹红霉素软膏等预防感染，或用纱布稍微保护一下。</p><p>	需要注意的是，如果伤口有出血情况，则需要进行按压，绝大多数伤口在20分钟之内就可以止血，如果超过20分钟无法止血，就要去医院就医。</p><p>	另外，较小的伤口不建议使用创可贴，因为创可贴包裹得过于严实，透气性不好，反而容易让伤口滋生细菌。</p><p>	</p><p>	<strong>砸伤、挤伤</strong></p><p>	严重指数：★★☆☆☆</p><p>	基本特点：砸伤或挤伤后，皮肤只出现轻度红肿疼痛，并无破损，可先观察，暂时不需处理。如果砸伤后出现皮肤破损，可按擦伤进行处理；如果发生皮肤淤紫、破裂甚至剧烈疼痛等重度砸伤和挤伤，则建议尽快就医。</p><p>	</p><p>	<strong>烧伤、烫伤</strong></p><p>	严重指数：★★★☆☆</p><p>	基本特点：按照烧伤的程度，在医学上可分为三度：I度烧伤，伤势比较轻微，通常会出现一定的红斑，但是没有水疱；II度烧伤，根据皮肤受累深度，又可以分为浅II度和深II度烧伤；III度烧伤，比较严重，皮肤呈皮革状，没有弹性，疼痛感会减弱或消失。</p><p>	医学上，烫伤也算烧伤的一种，二者处理原则大致相同。一般情况下，可以在日常生活中自行处理的多为I度烧伤。</p><p>	一般处理：</p><p>	1、用流动水冲洗5~10分钟，冲洗时间太长容易让伤口浸渍，即呈现我们常说的“泡发”状态。如果5~10分钟后疼痛没有减轻，则可以将毛巾浸湿后覆盖创面，这种做法对止疼很有效，也能够避免水流对皮肤的浸渍。</p><p>	2、在伤口表面涂抹抗生素软膏。需要强调的是，如果伤口出现水疱，则需要尽快就医。</p><p>	另外，不少人习惯烫伤后将牙膏涂抹在伤口处，这是没有科学依据的，且涂抹牙膏还会影响对伤口情况的判断。</p><p>	</p><p>	<strong>刺伤、咬伤</strong></p><p>	严重指数：★★★★☆</p><p>	基本特点：</p><p>	被细长的玻璃片、针、钉子、木刺等留下的刺伤，伤口一般较小、较深，有感染破伤风的风险，因此应尽早到医院处理。</p><p>	如果被动物咬伤，除了有患破伤风的风险，还可能会有患狂犬病的风险，一定要及时去医院注射狂犬疫苗。</p><p>	一般处理：</p><p>	1、到医院前可以先进行一些简单处理，如果伤口内未遗留刺伤物，可以先挤压伤口，使其流出一些血液。如果有残留的刺伤物，可以用消毒后的镊子取出。</p><p>	2、用生理盐水冲洗。</p><p>	3、外涂碘伏。</p><p class=\"ql-align-center\">	<strong>常用药品如何选？</strong></p><p>	</p><p>	<strong style=\"color: rgb(0, 0, 0);\">碘伏</strong></p><p>	碘伏是一种广谱杀菌药物，可直接涂抹在伤口处，使用方便。</p><p>	碘伏对大多数人体致病细菌、部分霉菌、原虫以及部分病毒等，都具有杀灭作用，可以用于患者皮肤烧伤、冻伤、创伤、擦伤的消毒，因其碘浓度较低，对人体没有毒性以及腐蚀性，而且对比碘酒、酒精，碘伏不会造成疼痛感，所以被广泛应用。在使用过程中要注意避免接触眼部。碘伏涂抹伤口后立即产生杀菌效果，后续无需反复涂抹，因空气中细菌浓度很低，不用担心空气污染伤口。</p><p>	<strong>碘酊</strong></p><p>	碘和碘化钾的酒精溶液。碘酊其实就是碘酒，二者是同一种物质，医学上通常称为碘酊，碘酒为俗称。</p><p>	因为碘酊对皮肤有刺激性，所以碘酊的应用相比碘伏要少得多，但是其杀菌效果非常显著。碘酊可以用于皮肤的消毒，比如抽血打针前使用碘酒消毒皮肤。</p><p>	值得注意的是，不推荐将碘酊涂抹于开放、溃烂的伤口处，因其存在一定的刺激性，反而可能影响伤口恢复。浓度为2%的碘酒常用于完整皮肤的消毒，但涂后2~3分钟必须用75%的酒精脱碘。</p><p>	<strong>酒精</strong></p><p>	医用酒精浓度在75%左右，具有一定杀菌消毒功效。生活中以酒精棉球最为常见，它们是由75%酒精和无菌棉球制作的。</p><p>	75%酒精确实能杀灭某些细菌，但对细菌芽孢和孢子以及霉菌没有效果，只能用于普通的手部表面消毒。酒精会对皮肤带来较强的刺激，疼痛感很强，一般而言，在伤口处洒酒精的疼痛为7-8级，医生建议广大家庭，日常选择更为温和的药品进行治疗。此外，长时间用酒精棉球擦拭皮肤消毒，会导致皮肤干燥粗糙。</p><p>	<strong>双氧水</strong></p><p>	双氧水是一种很强的氧化剂。医用双氧水可杀灭肠道致病菌、化脓性球菌、致病酵母菌，一般用于物体表面消毒。</p><p>	双氧水的好处是能够对于大而深且污染严重的伤口有效地起到消毒作用，医生进行清创操作时可能用到，在家庭场景中几乎用不到，也不需要储备双氧水。</p><p class=\"ql-align-center\">	<strong>怎样避免伤口感染？</strong></p><p>	<strong>注意伤口清洁</strong></p><p>	刚受伤后，可以根据伤口大小，选择是否去医院治疗。如果伤口面积比较小，可以用碘伏、碘酒等消毒物品对伤口进行消毒。如果擦伤的伤口上有小的污染物，可先用流动的清水或生理盐水冲洗伤口，之后再进行消毒。</p><p>	<strong>避免抓挠伤口</strong></p><p>	在伤口愈合之前，局部皮肤可能出现一定的瘙痒症状，这时尽量不要用手去接触伤口，以免手上的细菌对伤口造成感染，延长愈合时间。</p><p>	<strong>不要抠血痂</strong></p><p>	许多人认为血痂影响整体美观，所以就会用手抠除血痂。但如果伤口比较深，即使表面形成血痂，皮肤里面也没有完全愈合。这时用手抠除血痂，可能会让伤口再次出血，成为新的伤口，增加伤口感染风险。</p><p>	<strong>注意饮食清淡</strong></p><p>	如果受伤比较严重，那么在饮食方面还需要注意尽量以清淡为主，不要吃太多刺激性食物，以免影响伤口愈合。</p>','0','admin','2022-04-05 19:57:20','',NULL,NULL,'kepu-5.jpg'),(33,'三亿人上冰雪，出现急性腰痛怎么办？','1','<p>	<span style=\"color: rgb(112, 112, 112);\">2022年北京冬奥会各项赛事正如火如荼进行，普通大众对于冰雪运动的热情也随之高涨。无论对于驰骋冰雪赛场的运动员，还是普通大众，在感受冰雪运动带来的激情与快乐的同时，应特别注意可能出现的运动损伤。运动引起的腰痛就是常见的运动损伤之一，也是急性腰痛的一种。那么有哪些方法可以有效缓解急性腰痛？</span></p><p class=\"ql-align-center\">		<strong>冰雪运动的损伤</strong></p><p>	有文献指出，专业雪上运动员的总受伤率为3.49/1000（单位：人天，即人数与随访天数乘积的总和），而从受伤部位来看，躯干的受伤率为0.41/1000。</p><p>	所以说，极限运动实乃双刃剑，人类在享受挑战身体极限带来的快感时，潜在的危害不容小觑。只有了解运动损伤才能更好地预防和应对，今天就来讲一讲，运动时出现急性腰痛怎么办。</p><p class=\"ql-align-center\">	<strong>什么是急性腰痛</strong></p><p>	急性腰痛是腰部肌肉、筋膜、韧带等组织因外力作用突受过度牵拉，导致急性损伤或腰部小关节嵌顿而引发的一组病症，常在腰部肌肉突然强力收缩后，或因搬重物、受凉、持续固定体位、运动等情况下出现。腰椎稳定性不良是其发生基础之一。</p><p>	表现为腰骶部疼痛伴屈伸活动受限，体位变动时疼痛症状加重。其中，运动引起的腰部肌肉损伤并不少见，通过合理的康复训练可以得到良好的治疗效果。</p><p>	急性腰痛者一定需要卧床休息吗</p><p>	答案是否定的。有研究指出，急性腰痛患者应在耐受范围内维持规律的日常活动并进行一定强度锻炼，适当运动可以帮助缓解肌肉痉挛。</p><p>	数十年来，卧床休息被认为是急性腰痛患者的标准治疗方式。然而，近年来多项研究均证实，卧床休息并不会提升患者的恢复速度和程度。</p><p>	所以说，一动一静之谓道，适宜最好，只有将二者相结合才能达到更好的疗效。</p><p>	</p><p class=\"ql-align-center\">	<strong>休息的姿势</strong></p><p>	1、仰卧位，在膝关节和头下各放置一个枕头，将肩部抬高。</p><p>	2、侧卧位，位于上方的膝关节屈曲，在两侧膝关节之间放置一个枕头。</p><p>	3、俯卧位，在腹部下方放置一个垫子，将腰部适度弯曲。姿势应按照个人的舒适度进行调整。</p><p class=\"ql-align-center\">	<strong>居家缓解方式</strong></p><p>	理疗：居家环境下，可以选择热敷来缓解急性疼痛。热疗法可改善局部血液循环、缓解肌肉痉挛。可以选择上面提到的俯卧位，将热敷包加热至39~48℃，将其置于疼痛部位，持续20分钟左右。</p><p>	护具：工作或外出时可使用腰托来增加腰部稳定性。切莫长期依赖护具，日常生活中要学会使用腰部核心肌肉发力，这是人体自带的“天然腰托”。</p><p>	运动疗法：既可以预防腰痛的初次发生，也可以防止复发。</p><p>	<strong>1、扭伤3天内，静多动少</strong></p><p>	“核心收紧”运动——平躺并屈膝，维持腹部肌肉放松，将两只手的指尖放置在图中✖️位置，腹部依旧放松，此刻深吸一口气，慢慢呼气，在呼气的过程中，您的指尖往腹部的深处慢慢下压，如此呼吸和指尖下压2~3次后，您能感觉到指尖在腹部有明显的阻力，记住保持腹部肌肉全程放松。</p><p>	现在，保持您的手在这个位置，做一个轻微而缓慢的“缩肚脐”或“肚脐下坠”的动作，如果您感觉到指尖有东西在收缩变硬，说明您在使用“核心”。记住，这个过程都是轻微缓慢的深层肌肉收缩，而非整个腹肌都在用力收缩。</p><p>	开始可重复3~5组，每组10~20次，今后要学会在日常生活和运动中自然地使用这个动作，形似“气沉丹田”。</p><p>	</p><p>	<strong>2、扭伤3天到1周及以后，逐渐增加运动的比重</strong></p><p>	俯卧伸展——按照疼痛感受循序渐进，由静态到动态：静态俯卧肘撑→俯卧位到肘撑的动态反复3~5组，每组10~20次→静态俯卧直臂撑→肘撑到直臂撑的动态反复3~5组，每组10~20次→俯卧位到直臂撑的动态反复3~5组，每组10~20次。</p><p>	</p><p>	“虫子”运动——仰卧双腿屈膝90°至于空中，双手抬高与地面垂直，全程维持微缩肚脐，做上述核心收紧运动，然后将一只手往头部方向后伸，同时放下对侧腿（未接触地面），以此交替3~5组，每组10~20次。</p><p>	</p><p class=\"ql-align-center\">	<strong>预防和管理急性腰痛</strong></p><p>	研究指出，积极进行患者教育和自我管理有助于防止损伤的发生和复发。</p><p>	<strong>1、活动方式调整——搬重物的姿势</strong></p><p>	搬重物时，应利用深蹲动作，使用蹲姿拿起重物。在动作中应该维持脊柱中立稳定，尽量做到不弯腰，即靠腿部力量来搬重物。</p><p>	<strong>2、床垫的选择</strong></p><p>	中等硬度的床垫宜为首选。中等硬度床垫对卧床时疼痛及疼痛相关功能障碍的改善均要优于硬质床垫。</p><p>	<strong>3、运动前的准备</strong></p><p>	日常积极保持腰部肌群的锻炼。在运动前，做好腰部热身。</p><p>	普渡健康医生温馨提示：若症状持续加重，范围累及下肢，请适时就诊。希望大家在体验冰雪运动魅力的同时，都能做到遵循原则，安全运动。让我们冰雪相约，一起向未来。</p>','0','admin','2022-04-05 20:00:24','',NULL,NULL,'kepu-21.jpg'),(34,'每逢佳节胖十斤，你慌了吗？来看看普渡营养师的锦囊妙计','1','<p>	<span style=\"color: rgb(112, 112, 112);\">虎年春节到，朋友们是不是又开始担心体重问题了？确实，过节对“胖友们”不是太“友好”，尤其对于已经坚持减肥几个月的朋友们而言。那么节假日来了，到底该怎么做才能避免“胖十斤”呢？</span></p><p class=\"ql-align-center\">		<strong>“文武之道，一张一弛”</strong></p><p>	减肥也没有必要把弓弦绷得太紧，严格少吃或不吃。过节时，家里人聚个餐，“吃点好的”是正常的，不过疫情期间还是要少聚集。</p><p class=\"ql-align-center\">	<strong>不能过于放松</strong></p><p>	长期减肥的朋友们已经习惯了“吃七八分饱”的饮食，所以在大吃大喝或暴饮暴食后容易胃肠道不适。因此酌情、适度放松，对美食“浅尝辄止”，其实更有意犹未尽之美。</p><p class=\"ql-align-center\">	<strong>别内疚、别加戏</strong></p><p>	“胖友们”吃了也就吃了，千万别内疚或者给自己加内心戏，搞得自己很悲壮、很难过，完全没有必要！为何不换种思路，试试吃完后等价交换，第二天轻断食或稍微强化一下活动。</p><p>	</p><p class=\"ql-align-center\">	<strong>什么菜系热量更低呢？</strong></p><p>	我们来看看科学研究怎么说——</p><p>	各色美食“乱花渐欲迷人眼”，中餐、西餐、快餐、泰餐、越南餐……蒸煮、煎炸、烧烤……真真都是减肥的“拦路虎”呀！有没有研究，看看各种饮食中哪种是热量最低的呢？</p><p>	《英国医学杂志》还真做过一个有意思的横断面调查。在中国、巴西、芬兰、加纳、印度和美国6个国家中选择110家餐厅，既有全服务餐厅，也有快餐厅，研究223种常见的菜品，还包括芬兰五个地区的十份工作餐，通过弹式热量计测定不同菜品中所含的热量有多少。</p><p>	结果很有意思，同美国汉堡包、印度咖喱饭等相比，中餐的平均能量还是比较低的！工作餐的热量也低于外出就餐。当然中华饮食文化博大精深，就算200多种菜品全是中餐也不见得能完全代表中餐，中餐也要多蒸煮、少油炸！</p><p>	</p><p class=\"ql-align-center\">	<strong>果汁能不能喝？</strong></p><p>	大家都知道饮料好喝，但是热量高，那么果汁能不能喝呢？</p><p>	法国学者在2009~2017年所做的前瞻性队列研究中，调查了大约10万人，随访5年多，最后能得出什么结论？</p><p>	含糖的饮料，不论是可乐还是果汁，甚至就算是看起来很健康的鲜榨果汁，经常喝都可能增加患肿瘤的总体风险。</p><p>	那无糖的饮料是不是就没有问题呢？</p><p>	答案是也不尽然。</p><p>	无糖饮料不要经常喝或喝太多，因为其中一些甜味剂也容易超标，而且时间长了，能够影响到人体的肠道菌群，带来负面影响。研究还发现，常喝无糖饮料特别容易增加食欲。</p><p>	其实，普通的白水，每天饮用2000毫升左右就好！</p><p>	选择饮料的朋友们可以稍微看一下成分表，按照中国营养学会的推荐，精制糖每天最好不要超过50克。</p><p>	<span style=\"color: rgb(51, 127, 229);\">划重点：常喝饮料果汁，完全没有减肥效果哟，小朋友尤其要注意呀！</span></p><p>	</p><p class=\"ql-align-center\">	<strong>饮食放松，是浅尝辄止还是吃到爽？</strong></p><p>	饮食放松一下是正常的，人不是机器，减肥不能全靠毅力坚持，时间长了谁都受不了……适当放松，浅尝辄止，稍微给一点点小甜头，就会让今天心情的阈值高一些，但是吃到爽会不会更爽，到底要不要？</p><p>	不要！有很多朋友们平时饮食控制得很辛苦，吃到爽之后，马上伴随而来的就是吃完后的内疚感，有的人想歪招儿，例如抠吐、药物排油……很多时候是得不偿失的。</p><p>	在门诊，我们经常建议大家在特想吃的时候不用过于压抑，因为这是人的本性，但是，我们能做到的是浅尝辄止，比如，有的朋友喜欢吃零食，那就别吃太多，吃最小包装单位，或者一袋的四分之一，剩下的跟同事、朋友们分享一下，多好！大家尽量不要让自己吃到特别爽，毕竟经历过的“胖友们”都知道，吃的时候可能会让您感到既舒服又放松，吃完后伴随而来的内疚和空虚会让大家很不舒服，因此我们一定要小心。</p><p>	我们来看一项美国的研究，来自五家中心共234位13~19岁的少年接受了减重手术，该研究调查了少年们在接受手术后半年、一年、两年、三年的饮食失控情况。</p><p>	结果发现，减重手术作为减肥最狠的招儿，术后经常吃到爽的少年出现饮食失控的频率与减重手术后出现反弹明显相关。</p><p>	减肥要不要放松？</p><p>	<span style=\"color: rgb(51, 127, 229);\">划重点：可以适当放松，但不要吃到过爽，过犹不及，反而不爽。</span></p><p><br></p><p class=\"ql-align-center\">	<strong>适当运动，更易选择健康饮食</strong></p><p>	偶尔吃多很常见，不要内疚、等价交换，“动回来”可能是更好的应对策略。</p><p>	有研究发现，运动后人们更易选择健康饮食。</p><p>	一个非常有名前瞻性队列研究从2003~2015年，观察了运动对大学生饮食模式的影响。研究要求2680名大学生每周进行3天有氧运动，心率要达到最大心率的65%~85%，持续15周，主要的观察指标就是大学生们对食物的选择。15周后，有1859人完成了评估。结果发现，经常活动的人更容易选择健康的饮食模式。</p><p>	而且这种选择的倾向性，跟运动的时间和强度是有关系的。运动越多，强度越大，可能更偏向于选择健康饮食。这跟咱们生活中很相似，努力运动的人往往更喜欢偏健康的饮食。</p><p>	<span style=\"color: rgb(51, 127, 229);\">划重点：节假日偶尔多吃，稍微多动一动，既能保持健康，又有利于下一步选择健康饮食。</span></p><p>	</p><p class=\"ql-align-center\">	<strong>节假日，行为技巧也重要？</strong></p><p>	不管吃什么，怎么吃、称体重的行为技巧在节假日也有用。</p><p>	《英国医学杂志》发表了一项随机对照研究，在2016~2017年选择了272个人进行随机分组，采用不同的方法来度过圣诞节假期，研究在圣诞节假期前后进行4~8周行为干预对体重的影响。干预组需要进行行为干预，包括测量体重，绘制曲线，同时按照体力活动的热卡当量表格进行针对性锻炼。而对照组只是发放健康生活的宣传材料。</p><p>	结果发现，即使是简单的行为干预都比发放宣传材料有效得多。单纯的行为干预，哪怕不严格要求饮食，也会有效果，非常适合节假日减肥。</p><p>	</p><p class=\"ql-align-center\">	<strong>康大夫门诊的行为要求</strong></p><p>	第一次来康大夫门诊的“胖友们”，会有几个行为干预的“作业”，不用严格控制饮食，一周之后再来复诊，表现好的“胖友们”可以减三斤，表现特别好的能减得更多。行为干预的效果随着减重的时间越长，效果越突出。</p><p>	</p><p>	行为干预都包含哪些行为呢？</p><p>	一，少吃汤酱和外卖。</p><p>	二，三餐定时有加餐。</p><p>	三，先吃肉菜再吃饭。</p><p>	四，每口咀嚼三十下（牙不好慎用）。</p><p>	五，每天体重测一次。</p><p>	六，睡觉时间十一点。</p><p>	您不妨试试？</p><p class=\"ql-align-center\">	<strong>家人欢聚小技巧</strong></p><p>	家人欢聚时也有几个小技巧。</p><p>	一、做一个善于倾听的人。少举筷子多倾听。一举多得，既少吃了，又多倾听了他人分享的喜事，大家会觉得你这个朋友特别好，然后诸事顺遂。</p><p>	二、选择油少的菜，少喝汤、少饮酒。</p><p>	三、等价交换，第二天练回来。</p><p>	不过还是要强调，疫情期间尽量少聚集哟。</p>','0','admin','2022-04-05 20:03:08','',NULL,NULL,'kepu-6.jpg'),(35,'抗菌药物的常见使用误区，你中招了吗？','1','<p>	<span style=\"color: rgb(112, 112, 112);\">世界卫生组织将每年的11月18日~24日定为“提高抗微生物药物认识周”，以增强全社会对合理使用抗微生物药物的意识。当今，微生物耐药已成为严重威胁人类健康的全球性公共卫生问题。以细菌耐药为例，据专家推测，若不采取行动，到2050年，每年将有约1000万人死于超级耐药细菌感染。因此，维持现有抗菌药物的有效性，避免因药物滥用诱发耐药至关重要。</span></p><p class=\"ql-align-center\">		<strong>误区1：抗菌药物就是万能药！</strong></p><p>	小李：我头疼、嗓子疼、流鼻涕、浑身酸痛，还有点低烧，好难受啊~</p><p>	小李妈：赶紧吃点感冒药，对了，嗓子疼得吃阿莫西林或头孢，赶紧消炎！</p><p>	小李爸：吃药好得太慢了，抓紧输液，好得快！</p><p>	“感冒就要加用抗菌药物？并不是！“</p><p>	普渡药师来辟谣：上述场景相信大家都不陌生，然而这也是老百姓使用抗菌药物的常见误区之一。感冒是一种常见的上呼吸道感染疾病，多数由鼻病毒、副流感病毒、冠状病毒引起。大多感冒属于自限性疾病, 无特效药。多数患者通过多喝水、多休息、清淡饮食，加用对症药物即可恢复，只有明确存在细菌感染时才使用抗菌药物，通常情况下无需使用。而滥用抗菌药物不仅容易诱导耐药细菌产生，而且会产生消化道不适等多种副作用，因此需要由医生根据患者病情判断是否需要加用抗菌药物。</p><p>	</p><p class=\"ql-align-center\">	<strong>误区2：抗菌药物肯定越高级越好！</strong></p><p>	小李：大夫，听说头孢分好几代，您给我用最高级的那种。</p><p>	小李妈：对，用最贵的，只要能快点好就行。</p><p>	”抗菌药物越新、费用越高就说明疗效越好？并不是！“</p><p>	普渡药师来辟谣：患者急于康复的心情，我们很理解。但实际上，抗菌药物的选择和患者感染部位、感染发生场所、患者自身基础情况等多种因素息息相关。此外，不同抗菌药物的敏感菌群也不尽相同，在体内的分布情况也有差异。因此需要结合上述多种因素推测可能的致病菌，再结合药物本身的作用特点等因素才可恰当选择。用所谓新药或者贵药治疗不敏感的细菌，则如同射箭脱靶，毫无意义且浪费金钱。同时，高级别药物滥用还容易诱导细菌耐药，导致最后一道抗菌防线失守。</p><p>	</p><p class=\"ql-align-center\">	<strong>误区3：擅自改变用药方案也不会影响药物疗效！</strong></p><p>	小李：大夫，我吃了两天阿莫西林了，皮肤感染怎么还没好，不仅如此，我的胃还很难受，能停药吗？</p><p>	协和药师：请问您日常都如何使用该药品？</p><p>	小李：我每天按照处方吃3粒，但我工作太忙了，一般上午吃完饭就把三粒都吃了。</p><p>	“擅自改变医生的用药方案不仅可能会降低药物疗效，还可能增加药物的副作用，并且导致致病菌产生耐药性。”</p><p>	普渡药师说：遵医嘱服药是获得良好治疗效果的前提。药物疗效与体内药物浓度有关，部分抗菌药物需保证体内药物浓度在一定时间范围内，都在某一特定值之上，才可发挥较好的疗效。因此患者需严格遵守处方中的用药频次（如每日用药3次，应尽量保证间隔8小时服药一次）。若白天服药间隔时间过短，会导致夜间体内药物浓度无法达到有效抑菌浓度。</p><p>	除了以上3个误区之外，抗菌药物的使用误区还包括患者因担心再次感染，自行长期预防性使用抗菌药物，延长药物使用时间等，这些误区的存在可能会导致抗菌药物相关性腹泻等不良反应，同时也为细菌耐药埋下了隐患。</p><p>	合理使用抗菌药物、遏制耐药需要我们每一个人付出努力。滥用抗菌药物将导致今后无药可用。让我们携手努力，遏制耐药，描绘健康中国的美好蓝图。</p>','0','admin','2022-04-05 20:05:07','',NULL,NULL,'kepu-7.jpg'),(36,'关于种植牙，您想知道的都在这里~','1','<p>	<span style=\"color: rgb(112, 112, 112);\">牙齿缺失会带来很多问题，如影响咀嚼、语言功能等，造成牙齿移位、骨吸收、软组织萎缩等后果，牙齿缺失不仅影响功能和美观，还会损害身心健康。近年来，随着现代科技的发展，牙科技术的进步，大众越来越重视口腔保健。您是否为选择什么治疗方案来修复缺失的牙齿而发愁？您是否担心种植修复存在风险？您是否了解种植牙的恢复期有多久？</span></p><p class=\"ql-align-center\">		<strong>牙齿缺失的修复方法</strong></p><p>	牙齿缺失的修复方法主要有三种，分别是活动义齿、固定义齿和种植义齿。</p><p>	活动义齿，优点是无须手术、修复耗时短、创伤较小、磨牙较少。但需摘下清洁，也存在一定缺点，如对邻牙可能造成一定损害，长期可能导致局部龋齿或牙周松动、骨吸收。</p><p>	固定义齿，优点是镶牙较快，舒适度较好，缺点是会损失牙齿表面最坚硬的牙釉质，桥体下方比较难清洁，长时间可能导致局部龋齿，甚至牙周问题、牙齿脱落等。</p><p>	种植义齿可以追溯到古代，古代曾把象牙雕塑成牙齿形状镶嵌到颌骨里面。近代起出现了各种各样金属材质的种植体，在种植体上面镶牙，但由于种植体材料、制造和临床技术的局限，失败率较高。到了现代，高精度制作的种植体出现，表面经过特殊处理的钛合金材质与人体适应性良好。</p><p>	</p><p class=\"ql-align-center\">	<strong>种植牙的适应症</strong></p><p>	口腔种植适用于无牙颌、多牙缺失、单牙缺失、活动义齿功能不佳等人群。</p><p>	当无牙颌患者牙槽骨比较好，有足够的种植区域时，采用种植体支持固定修复的方法，可以达到良好的功能和美学的协调，但是费用较为高昂。如果牙槽骨条件不够好或软硬组织缺失比较多，外形欠丰满，可做种植体支持覆盖义齿，这种义齿虽然可以摘卸，但牢固程度可以和固定义齿相媲美，同时费用相对种植固定义齿低。其次，它具有专门的支撑基托，修复欠缺的组织，视觉效果更丰满，外观更年轻美观。同时，容易摘卸、更方便清洁。</p><p>	连续多牙缺失，可以做活动义齿或种植义齿。种植方案可以选择在缺隙前后两端植入两个种植体，完成种植体支持的固定桥；也可以选择缺几个牙就种几个种植体，种植体支持的单冠修复，这样牙齿之间可以方便使用牙线进行清洁和维护。如果骨质条件不够好，且不想植骨，可以种植短种植体和窄径种植体，适当增加种植体数目。</p><p>	单牙缺失，可以做种植体支持的单冠修复，前牙还可以做种植体支持的全瓷修复更为美观。</p><p>	</p><p class=\"ql-align-center\">	<strong>局部禁忌症</strong></p><p>	1、重度骨缺损：如果不能接受植骨，牙槽骨不能容纳可用种植体，即不能种植。</p><p>	2、咬合或颌间关系不良：需先调整上下颌咬合关系，待有足够的空间再进行种植。</p><p>	3、 颌骨发生病理性改变。</p><p>	4、 病理性黏膜病变：需要先行治疗后方可进行种植修复。</p><p>	5、 头颈部放疗术后：创伤或感染可能影响种植体骨结合。</p><p>	6、 干燥综合征：这类患者可能口腔自洁功能较差，并且长期全身用药，会影响种植修复。</p><p>	7、尚未控制的牙周病或口腔卫生极差：这类患者需先进行牙周系统治疗，再进行种植修复。</p><p>	</p><p class=\"ql-align-center\">	<strong>全身健康相关因素</strong></p><p>	种植修复需综合考虑患者全身情况、营养状态及年龄等因素，如是否存在内分泌代谢障碍、血液系统疾病、心血管系统疾病等，以及是否长期服用特殊药物、存在吸烟及饮酒史等。</p><p>	无论是电子烟还是细烟，都含有影响组织愈合的尼古丁，建议在术前和术后戒烟或减少吸烟量。</p><p>	</p><p class=\"ql-align-center\">	<strong>种植术后注意事项</strong></p><p>	1、常规种植病人，术后口服5天抗生素，植骨的病人可能需要服用7天的抗生素，必要时静脉输入抗生素。</p><p>	2、术后需要马上冰敷，48小时后可以热敷，以减轻肿胀、疼痛以及局部淤青。</p><p>	3、术后应做好含漱，保持口腔清洁；口腔卫生如果没有维护好，容易造成局部感染。</p><p>	4、术后应食用凉一些、软一些的食物，尽量不使用手术部位吃东西，不食用坚硬的食物。</p><p>	5、 良好的休息有助于尽快恢复。</p><p>	</p><p class=\"ql-align-center\">	<strong>术后饮食</strong></p><p>	术后不宜食用坚硬食物（如薯条、坚果、苹果等），酸性食物（如番茄、柠檬、酸橙、橙汁等）以及辛辣食物。</p><p>	可食用：燕麦片、面条、大米、鸡蛋、牛油果、香蕉以及健康的果汁或冰沙等。</p><p>	</p><p class=\"ql-align-center\">	<strong>种植会产生并发症吗</strong></p><p>	种植手术相关的并发症和术后反应有：感染；天然牙齿、血管或神经损伤；局部麻木，牙龈、嘴唇和脸颊肿胀、疼痛、瘀伤等。种植修复经过一段时间使用，可能出现种植体周围炎或周围黏膜炎，可能伴有局部疼痛、红肿、出血、异味等，甚至出现发烧等不良反应，因而种植修复后要注意口腔卫生，刷牙漱口，使用间隙刷、牙线等工具清洁牙齿。除了日常维护外，还需要进行专业维护，定期复诊检查、清洁等。</p><p>	</p><p class=\"ql-align-center\">	<strong>种植牙如何维护</strong></p><p>	1、保持口腔卫生，术后每天用不含酒精的漱口水含漱2至3次。</p><p>	2、牙刷不能触及之处，使用牙线、间隙刷、冲牙器等辅助以彻底清洁。</p><p>	3、定期看牙医，修复完成后1周、1个月、3个月、6个月、12个月复查。之后每半年/一年复诊一次为宜。</p><p>	4、不要吃硬的食物，以免损坏牙冠和种植体。</p><p>	5、避免吸烟。</p>','0','admin','2022-04-05 20:06:33','',NULL,NULL,'kepu-8.jpg'),(37,'失眠只能硬抗？五大法宝了解一下！','1','<p>	<span style=\"color: rgb(51, 51, 51);\">您经历过失眠吗？您周围有为失眠苦恼的亲友吗？如果答案是肯定的，您恐怕会有一个感慨“失眠真愁人！”一愁说不清，经常是不知为啥就是睡不着；二愁没处看：西医？中医？按摩？保健？众说纷纭。最终采取的解决办法往往是“先忍着吧”，殊不知其结果往往更严重。今天就让我来为大家解开这两团愁云。</span></p><p class=\"ql-align-center\">		<strong>失眠的原因</strong></p><p>	有个童话故事叫“豌豆上的公主”，讲厚厚的床垫下有一颗豌豆，都可以让公主睡不好，这也是我们对失眠者的常见误解，认为他们是太过娇气或无病呻吟。</p><p>	试想我们的床垫下垫的若是几块石头，几颗钉子，以及烧红的炭，我们还会这样说吗？</p><p>	而这正是失眠常见的三类病因：石头即各种可能影响睡眠的药物或烟、酒等精神活性物质；钉子即躯体疾病，各个器官系统均有些疾病会出现失眠症状，尤其是神经精神科疾病；烧红的炭则比喻人的不良心理状态，包括多种干扰正常睡眠功能的情绪、想法和行为。</p><p>	这三类问题都是实实在在地在干扰着睡眠，它们可以并存，还经常互相促进形成恶性循环。比如有的患者罹患脑梗后，大脑功能有一定损伤，同时又因为生病苦恼，躯体疾病及不良心理状态共同引起了失眠，患者自己也深感失眠不利于健康，每天总想补觉，失眠反而越来越重。或者有人因为焦虑症睡不好觉，试着喝酒助眠，长期形成酒依赖，因为酒对大脑的损伤而导致更难纠正的失眠。所以我们要知道，失眠往往是各种原因交织在一起逐渐演变才出现的症状，一百个人就有一百种图解，繁简不一。</p><p class=\"ql-align-center\">	<strong>如何应对失眠</strong></p><p>	好消息是，虽然失眠原因复杂，却不难对付，需要的只是充分的认识和管理。这里提出最重要的五大法宝：</p><p>	<strong>一、及时合理应对：</strong>失眠持续时间未超过1个月尚属急性失眠，拖延和不当的应对会使失眠更加复杂迁延。因此发现自己出现失眠时即应正视，采取积极合理的的应对措施。但无需着急害怕，要知道多数情况下失眠只是身心失衡的一个小小警报信号，提醒我们要采取必要的行动，而其本身并不危险。</p><p>	<strong>二、分析相关疾病和药物：</strong>需考虑（1）自己已有的疾病是否近期有波动，（2）失眠前有没有新加用某种药品或保健品，（3）近期有没有新出现某些症状，如神经科症状（头痛、头晕、健忘、感觉或运动异常等）、精神科症状（情绪低落、失去兴趣、注意力不集中、持续担忧、紧张害怕、精神亢奋、躯体焦虑症状如心慌尿频肉跳等），（4）有没有长期存在而被忽视的睡眠相关症状（睡眠中呼吸暂停、憋醒或梦中激烈的动作等）。将这些情况告知相关科室的医生，采取适当诊治。如果仍不确定应该就诊的科室，则可以选择神经内科或心理科请医生协助自己进行分析并处理相关病因。</p><p>	<strong>三、注意生活习惯：</strong>不提前上床，不长时间补觉，不在日间长时间卧床，规律饮食，适度活动，不在睡前做激烈运动。</p><p>	<strong>四、缓和不良心理状态：</strong>即便没有焦虑症或抑郁症，单纯害怕睡不着或迫切要睡个好觉的心态也足以干扰睡眠，不可掉以轻心。我国失眠治疗指南指出，1个月以上的失眠都应当辅以心理行为的干预，国际比较认可的治疗方式称为CBT-I（失眠的认知行为治疗），必要时可寻求有此技能的心理医生给予帮助。</p><p>	<strong>五、适当使用药物控制症状：</strong>现今有多种助眠药物，中药西药均有，特性和效果各异，可以在有经验的医生指导下使用，这些医生多在心理科或神经内科。使用药物的目的是减轻失眠的不利影响，使患者纠正失眠的过程少些困难。一直被抹黑的安定类药物其实是久经考验的武器，安全而有效。安定类药确实有一定成瘾性但并不强烈，很多“戒不掉”的现象其实只是因为某些病因没有去除，“床垫下仍有异物存在”。反过来我们也要避开这个常见误区：既然用药能睡着，就放任自己只依靠药物，懒得去改善生活习惯和调整自身心理状态，这样使用药物确实不当。</p><p>	回到开头的比喻，床垫下的石头和钉子我们可以用第二法宝扫除，炭火可以用第三、四法宝扑灭，而且一、四、五法宝还可以加厚床垫，再有影响失眠的因素来袭时我们就更容易抵御，自然可以高枕无忧啦！</p>','0','admin','2022-04-05 20:08:40','',NULL,NULL,'kepu-9.jpg'),(38,'请查收这份更年期健康生活指南','1','<p>	<span style=\"color: rgb(51, 51, 51);\">随着人类期望寿命的延长，中老年女性已成为庞大的群体。截至目前，40~60岁女性占我国人口的11.28%，预计到2030年，我国50岁以上的女性将增加到2.8亿人。目前，更年期女性的健康保健在世界范围内备受重视，男性更年期也引起了社会关注。在刚刚过去的“世界更年期关怀日”里，普渡健康医院专家对于更年期面临的更年期综合征等诸多问题给予了解答，同时送上一份更年期健康生活指南，快来查收！</span></p><h2 class=\"ql-align-center\">		<strong style=\"color: rgb(51, 127, 229);\">人人都会经历更年期</strong></h2><p>	更年期没有特别明确的定义，一般指从绝经前4~5年到绝经后10年的一段时间（自40岁后月经紊乱起）。随着我国人口寿命的延长，每个人都会经历更年期。</p><p>	以北京市为例，北京市女性平均寿命约为85岁，这就意味着几乎所有女性都会经历更年期，且女性的一生当中约有三分之一甚至更多的时间，要在绝经状态中度过。</p><p>	近几年，男性更年期也得到了社会关注，和女性有很大不同的是，由于男性睾丸功能和雄性激素水平的下降持续时间相对更长，所以男性在更年期表现出来的问题没有女性突出，有些男性在80岁时，睾丸功能还会存在。</p><p>	处于更年期的女性，卵巢功能开始衰退，体内雌、孕激素开始波动、下降，直到卵巢功能完全丧失，雌、孕激素降低到一定水平，会出现绝经。绝经前后会出现一系列精神及躯体症状，如植物神经功能紊乱、生殖系统萎缩等症状，即更年期综合征。大部分女性可以平稳度过这一阶段，但约有四分之一的女性症状比较严重，会影响生活和工作，需要治疗。近年来更年期综合征的发病年龄有提早的趋势，发病率也逐年上升。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">更年期各阶段注意事项</strong></h2><p>	在女性更年期的过程中，早期的一段时间被称作围绝经期。围绝经期与更年期的区别是围绝经期有着非常确切的时间标志点，如月经迟到、稀发，之后1年不来月经，就认为是绝经，而更年期是指从育龄期到老年期的过渡时期，没有确切的标志点。</p><p>	进入围绝经期后会出现月经紊乱，并伴随着出现潮热出汗、脾气暴躁、睡眠障碍等一系列更年期症状。</p><p>	其中，更年期早期潮热出汗症状的发生率高达80%左右，其中重度患者占患者总数的10%~20%。潮热出汗是更年期妇女最为常见的典型症状，由卵巢功能衰竭、雌激素水平下降，引起的体温调节失衡导致，也被称为血管舒缩症状（VMS）。潮热症状发作时，面部可见明显发红发热，常伴上半身出汗及心跳加速，有些人会极大量出汗。</p><p>	到了更年期中期阶段，女性会出现泌尿生殖系统萎缩的情况，如阴道黏膜萎缩、变薄，阴道干涩等，很容易引起老年性阴道炎和泌尿系统感染。在这段时间，憋不住尿的尴尬时有发生。女性更年期综合征根本原因是缺乏雌激素，在医生的指导下，掌握好适应症和禁忌症，适当地补充小剂量雌激素，会缓解上述大部分更年期症状。</p><p>	需要注意的是，年轻妇女因手术切除双侧卵巢，或经放射治疗后，也可出现更年期综合征。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">警惕更年期出现的慢性病</strong></h2><p>	更年期的尾声即将步入老年阶段。由于激素水平变化，大部分曾经没有暴露出来的身体问题都会在这个阶段显现出来。比如骨质疏松、高血脂、高血压、糖尿病、冠心病、神经系统退化、认知功能障碍和记忆力减退等，这些问题如果不及时发现和解决，都会变成不可逆疾病。</p><p>	<strong>骨质疏松：</strong></p><p>	女性在更年期时因卵巢功能衰退，导致体内雌激素水平大幅下降，致使其骨骼中的钙质发生快速流失，很容易引发骨质疏松；而骨质疏松不仅会导致骨骼及肌肉出现疼痛，更会大大增加发生骨折的风险。因此，当女性步入更年期，一旦发现有骨质疏松的迹象出现，应积极采取有效措施进行应对，如补充钙剂。</p><p>	<strong>心脑血管疾病：</strong></p><p>	女性体内的雌激素对于心脑血管会起到较强的保护作用。当雌激素水平受女性更年期卵巢功能衰退的影响发生断崖式下降时，其对于血管的保护作用会逐渐丧失。因此更年期的女性也更容易出现心脑血管疾病。对此，处于更年期的女性应学会调整饮食结构，避免或减少对“三高饮食”的摄入、增强日常体育锻炼、定期体检，一旦发现心脑血管异常，应及时采取有效的应对措施。</p><p>	<strong>高血压：</strong></p><p>	更年期高血压也与雌激素水平大幅下降密切相关。雌激素水平下降使血管弹性下降，进而引发的高血压属于症状性高血压。很多更年期女性在日常血压检测时都是正常的，只是在情绪激动、睡眠不好的时候测量才会出现血压升高，这种高血压往往是暂时的，顺利度过更年期后血压就会恢复正常。但是，并不是所有的更年期高血压在更年期后都能自行恢复。</p><p>	另外，随着年龄增长，血管弹性逐渐下降，如果年轻时血脂较高，还会增加动脉粥样硬化的风险。所以，当发现自己的血压不稳定时，一定要定期监测，调整生活状态，如果血压持续处于较高水平，一定要去医院就诊，千万不要轻视更年期高血压。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">更年期健康生活指南</strong></h2><p>	针对更年期症状，医疗方面的干预只有在健康生活方式基础上才能起到作用。那么怎样才是更年期应该遵循的健康生活方式呢？</p><p>	<strong>1、饮食与营养</strong></p><p>	女性进入更年期，应适当减少碳水化合物的摄入量，总热量的摄入应较年轻女性减少。饮食特点应为低热量、低脂肪、低盐、低糖。</p><p>	增加膳食纤维摄入量，每日20~30g。粗细粮应搭配食用，如将粗粮、杂粮和全谷物食品相搭配，最好能达到每日50~100g，每周食用5~7次。</p><p>	<strong>微量元素的每日摄入推荐量：</strong>钙1000mg；铁15mg；钠少于6g，高血压和冠心病患者以5g以下为宜。</p><p>	<strong>2、向吸烟与被动吸烟说不</strong></p><p>	吸烟引发的恶性肿瘤以肺癌为首。对女性的影响还包括使绝经时间提前或月经周期改变。二手烟中含有大量有害物质及致癌物，不吸烟者暴露于二手烟之中同样会增加多种吸烟相关疾病的发病风险。二手烟暴露可以导致不吸烟女性患肺癌、乳腺癌、冠心病、脑卒中的风险增加。</p><p>	<strong>3、限制饮酒</strong></p><p>	酒精可促进血液循环，但是饮酒要限量，45~59岁中老年人，酒精摄入量每日应不超过10g为宜。</p><p>	<strong>4、运动</strong></p><p>	更年期女性在运动锻炼中应尽量避免“肌肉-关节-骨骼”系统损伤，锻炼的最佳方式为每周至少3次，每次30分钟，中等强度。另外，每周增加2次额外的肌肉力量锻炼，益处更大。</p><p>	建议每天进行累积相当于步行6000步以上的身体活动。根据运动时的心率来控制运动强度，中等强度的运动心率一般应达到150次/分钟。</p><p>	<strong>5、体重管理</strong></p><p>	更年期女性正常的体重指数应保持在18.5~23.9kg/㎡。</p><p>	体重指数的计算方法：体重指数（BMI）=体重（kg）/身高（㎡）。当BMI≥24为超重，BMI≥28为肥胖，女性腰围≥80cm为腹部脂肪蓄积的界限。肥胖会对身体健康造成很大的影响，在绝经后女性中，肥胖已成为一个日益严重的问题；体重若减轻5%-10%，就能有效改善与肥胖相关的多种异常症状。</p><p>	不可突击减肥。轻度的成年肥胖患者，每月可稳定减肥0.5~1kg，中度以上成年肥胖患者，每周可减少体重0.5~1kg。热量的摄入多于消耗，是肥胖的根本成因。对于热量的控制要循序渐进、逐步降低的原则，同时增加热量消耗。安全起见，建议每日减少热量125~250kCal。</p><p>	<strong>6、睡眠</strong></p><p>	更年期女性每日需要7~8小时夜间睡眠时间，午睡时长为15~20分钟为宜。</p><p>	<strong>7、性生活</strong></p><p>	性生活是更年期女性生命活动的一个组成部分。如果更年期女性过早地终止性生活，不仅对本人的身心健康有影响，而且会影响夫妻感情和关系，影响家庭关系和谐。应结合患者的个体情况及当前需求，选择合适的性激素疗法、非激素疗法、性心理治疗等，制订有针对性的治疗方案。</p><p>	作为晚年时期重要的组成部分，更年期面临的问题是多方面、多系统的，更年期的保健措施也应该立足多层次、多维度。预防为先的健康理念，可以帮助更年期女性减少更年期相关疾病的发生。</p><p>	</p><p>	<strong>普渡医生温馨提示：</strong></p><p>	目前市面上存在大量与更年期相关的保健产品，多数缺乏有效性和安全性的全面评估，患者一定要在就诊后遵从医嘱，根据医生的建议服用相关药物等。</p>','0','admin','2022-04-05 20:12:38','',NULL,NULL,'kepu-10.jpg'),(39,'能让人变美的“毒药”，想了解一下吗？','1','<p>		<span style=\"color: rgb(51, 51, 51);\">看到肉毒毒素这四个字（botulinum toxin，Botox），很多人的第一反应肯定是：“哇塞！毒药！”这个反应一点都没错。肉毒毒素是肉毒杆菌产生的神经毒素，能抑制神经末梢释放乙酰胆碱，引起肌肉松弛麻痹，尤其是呼吸肌麻痹。但这种“毒药”同样也能造福人类，我们来一起了解一下它吧。</span></p><h2 class=\"ql-align-center\">		<strong style=\"color: rgb(51, 127, 229);\">长期使用肉毒毒素会中毒吗？</strong></h2><p>	撇开剂量谈毒性，那都是假行家，就像吃1个馒头能活命，吃100个馒头能要命。在大家从医疗美容的角度认识肉毒毒素之前，它已经被用于神经科、眼科、口腔科的一些治疗，主要用于治疗面部痉挛和其他肌肉运动紊乱症。1986年，加拿大眼科教授Alan Scott发现肉毒毒素能让患者眼部的皱纹消失，引发了美容史上的“Botox革命”，众多爱美人士趋之若鹜。</p><p>	医学界发现，肉毒毒素只要在合理的剂量范围内使用，那真是“人类福音”——瘦脸、瘦腿、瘦肩膀，改善皱纹、瘢痕，另外，在治疗腋臭、镇痛等方面也有非常好的效果。</p><p>	但肉毒毒素终归是有毒物质，很多人担心长期使用会不会把自己“送走”。那么，要把一个70kg的成年人“送走”，究竟需要多少瓶肉毒毒素？</p><p>	成年人的肉毒毒素致死剂量约为2500～5000u，按照市场上常用的肉毒毒素剂型（100u/支）计算，大约需要25～50支。而临床上的治疗，一般一次最多用3瓶。所以，只要正规使用肉毒毒素，大多都是安全的。而且进入体内的肉毒毒素会随着时间的流逝慢慢被代谢掉，并不会一直蓄积。</p><p>	此外，国家对于肉毒毒素的管理也十分严格，获批进入正规医院的肉毒毒素都属于特殊管理的毒麻药品，须经严格的处方限制才能购买和使用。药品监管部门还作出了“只能在有处方权的医生监护下注射使用”“不能在药店或美容院销售”等诸多严格的限制规定。</p><p>	正规的产品和正规的使用，是安全的可靠保障。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">打完肉毒毒素还要注意这些</strong></h2><p>	虽说医学美容让人“美美哒”，但是所有的医美操作都是“有风险的投资”。肉毒毒素注射也属于医疗行为，可能会引起不良反应，如过敏反应、出血、瘀斑、感染、肌肉力量改变或外观变化等。</p><p>	如果发生不良反应，需要及时到具有正规资质的医疗机构就医。一般来说，随着肉毒毒素的代谢，不良反应可以慢慢消失。此外，肉毒毒素的药力可以维持6个月到12个月不等，短时间内不建议多次注射，注射的剂量和频次一定要谨遵医嘱。</p>','0','admin','2022-04-05 20:14:36','',NULL,NULL,'kepu-11.jpg'),(40,'国产灭活疫苗第三针，会出现什么效果？','1','<p>	<span style=\"color: rgb(51, 51, 51);\">据《北京日报》消息，按照国务院联防联控机制统一部署，为进一步加固免疫屏障，经前期筹备，北京市各区已启动新冠病毒疫苗加强免疫接种，提供“加强针”服务。近来新疆、甘肃、内蒙古、陕西等地相继出现确诊病例和无症状感染者。国产灭活疫苗对新冠病毒尤其是德尔塔变异株是否具有保护作用？注射“加强针”效果如何？今天，普渡健康医院专家就这些大家普遍关心的问题作出了解答，一起来学习吧！</span></p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">预防德尔塔，灭活疫苗有效性高</strong></h2><p>	已发表的广州数据表明，注射过国产灭活疫苗的人群具有更强的预防作用。和没有接种过疫苗的人群相比，注射过新冠疫苗的人群的德尔塔新冠感染危险只有0.25 (0.09-0.68)，也就是感染风险降低了75%左右，疫苗有效率约70%。对重症或危重症的人群进行分析发现，重症或危重症均发生在没有注射过新冠疫苗的人群。说明国产疫苗不仅对预防新冠（包括德尔塔变异株）有效，而且对预防重症的效果尤其突出。</p><p>	对于还在犹豫、观望、尚未注射新冠疫苗的读者，请尽早安排疫苗接种，绝大部分慢性疾病患者都不存在注射的禁忌。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">疫苗注射6个月后，抗体滴度显著降低</strong></h2><p>	灭活疫苗在注射后，虽然可以达到很好的阳性率和保护作用，但研究发现，6个月后，抗体滴度和注射后相比，均有显著下降（阳性率下降至约16-35%）。研究数据表明，新冠疫苗并非一劳永逸，确实需要注射加强针。但最佳的接种方案有待进一步研究和确定。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">第三针并非简单重复，</strong>	<strong style=\"color: rgb(51, 127, 229);\">抗体反应滴度高时间久</strong></h2><p>	为了探讨如何打好第三针，科兴疫苗分4组进行对比研究（方案1：0，14天，42天；方案2：0，14天，192天；方案3：0，28天，56天；方案4：0，28天，208天）。研究发现虽然2剂注射后6个月左右，抗体滴度明显下降，但第3剂之后不仅可以迅速升到高水平的抗体滴度，而且比第2针更强，是第2针的3-5倍（图示红线，方案2和方案4）。另外，第三针注射6个月后，多数还保持阳性滴度，抗体阳性率约61-66%（篮线，方案1和方案3）。可以预见，6个月后注射第3剂，不仅可以获得更高的抗体滴度，在随后的6个月中可望保持较高的阳性水平。</p><p>	目前，第三针疫苗的研究主要在同一产品或相同技术路线的产品中开展，第三针新冠疫苗是否可以采用不同技术路线的疫苗，还有待进一步研究。</p><p>	总结一下，国产灭活新冠疫苗的预防效果非常确切，对德尔塔变异株的防护作用也有高达70%的效果，对重症和危重症风险具有较强的预防作用。目前国产灭活疫苗采用的2剂注射方案，抗体滴度随时间延长会逐渐下降，但机体具有很好的免疫记忆能力。在接种完成后6个月左右注射第3剂加强针，并非简单的重复，可获得较高抗体滴度并可望长时间维持在阳性水平。</p><p>	最后也要强调，抵御新冠病毒，除了接种疫苗，在日常生活中，戴口罩、保持社交距离和注意个人卫生都是不能松懈的。</p>','0','admin','2022-04-05 20:17:04','',NULL,NULL,'kepu-12.jpg'),(41,'孩子不爱喝水怎么办？一文详解孩子喝水的五大误区','1','<p>	妞妞是个幼儿园小班的小姑娘，平时喜欢跑跑跳跳，吃饭也非常积极，但是这几天妞妞的家长有点着急——妞妞上学后总是不喜欢喝水，更不爱喝白开水，只有运动后大量出汗或是渴极了的时候，才能灌下一整瓶纯净水。妞妞妈妈认为多喝水可以排毒，但纯净水没有营养，为了替代纯净水，常给孩子喝牛奶、鲜榨果汁，或在吃饭的同时多喂白开水，可是妞妞越来越不开心。和幼儿园老师沟通后，妞妞妈妈才知道孩子不愿意在幼儿园上厕所，喝了水又憋不住，因为不想尿裤子才不愿意多喝水……</p><p>	孩子不爱喝水的难题，相信很多家长都不陌生，然而并非所有人都懂得“正确”地培养孩子喝水的习惯，今天，就让我们一起来了解孩子喝水的五大误区，探讨如何培养正确的喝水习惯。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">误区一：孩子不渴，也需要喝水吗？</strong></h2><p>	妞妞平常总是不爱喝水，只在口渴时才大量喝水，这是不正确的喝水习惯。有些家长可能会有疑问，孩子不渴，也需要喝水吗？正确的饮水习惯是在孩子不渴的时候也应规律地定时喝水。</p><p>	当人体感受到口渴时，成人的水分丢失量已经是体重的2%，孩子的水分丢失量甚至可以达到体重的2%~5%，这时人体已经处于慢性缺水状态。缺水将导致血液浓缩、血容量下降，进一步发展不仅会造成细胞脱水，还会引起食欲降低、精神萎靡、四肢乏力等症状，长期缺水更会影响孩子的生长发育，进而引发疾病。</p><p>	因此，家长平时要注意给孩子养成良好的饮水习惯，引导孩子定时、定量喝水，不要等到口渴时才喝水或者一次喝过多水。家长应以身作则，作好表率，也可以通过小游戏、语言诱导、更换孩子喜欢的水杯等方法，培养孩子主动喝水的好习惯。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">误区二：给孩子多喝水，可以排毒吗？</strong></h2><p>	妞妞妈妈认为喝水可以排毒，觉得孩子喝水越多，身体越健康。我们都知道多喝水可以促进新陈代谢，那么多喝水可以排毒吗？</p><p>	实际上对于人体，水所充当的角色主要是运输其它营养物质的载体，适当地多喝水可以促进胃肠蠕动，加快体内废物排出。但强迫孩子喝过多的水，不仅会占据胃容量，影响孩子食欲，长此以往也可能导致孩子营养不良。当摄入水分过多，超过肾脏排出的能力，还会导致孩子体内电解质紊乱。</p><p>	孩子主动喝水喝得多，就是好事吗？</p><p>	有时孩子主动多喝水家长也需要警惕！尿崩症的孩子会出现大量喝水和大量排尿的症状，每天能喝掉超过4升水，不喝水时还会出现烦躁不安、渴感非常明显的状况。如果发现孩子出现这种情况，需要立即就医，积极寻求专业医生的帮助。</p><p>	根据《中国居民膳食指南（2016）》，成人每日推荐饮水量应达到1500~1700 毫升，而孩子所需的饮水量则因年龄、饮食结构、生活气候、日常活动量等不同有较大差异，这就需要家长细心观察孩子的精神状态、是否会口渴、小便量和颜色等，遵循“少量多次”原则指导孩子正确喝水，防止出现饮水过多或过少。</p><p>	在体内水分流失较多的情况下，孩子需要适当增加喝水量以补充水分，包括剧烈运动后、中暑时、发烧时、夏季出汗较多时等。但需要特别注意的是，应避免在剧烈运动后立即饮用大量水，因为这会加重心脏、肾脏的负担，严重时甚至造成水中毒。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">误区三：只喝纯净水，孩子会不会营养不足？</strong></h2><p>	妞妞妈妈认为，纯净水去除了人体必需的矿物质，包括水中的常量元素和微量元素，长期饮用纯净水会造成孩子营养摄入不足。</p><p>	实际上并非如此，水在人体内发挥的主要作用是作为载体促进新陈代谢，而不是提供营养。对于孩子来说，生长发育所需的营养和钙、镁等矿物质仅通过喝水是远远不够的，而是主要来源于食物。在世界卫生组织(World Health Organization, WHO)的《饮用水水质准则》中，对水中的矿物质含量也没有提出要求。</p><p>	那么该给孩子喝什么样的水呢？</p><p>	无论是白开水、纯净水、矿泉水，只要符合饮用水卫生标准，都可以满足补充水分的需求。</p><p>	生活中，白开水是家长们最经济方便的选择，但烧开的水不应放置太久，而是要尽量现烧现喝，避免细菌滋生，并且尽量要给孩子饮用晾好的温水。但有些地区由于地下水含氟量较高，家长可以选择处理过的饮用水，避免孩子出现氟斑牙，甚至影响骨骼的发育。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">误区四：多给孩子喝牛奶、果汁，还需要喝水吗？</strong></h2><p>	妞妞妈妈认为牛奶和鲜榨果汁营养丰富，而且更符合孩子的口味，可以作为水的替代品，因此给不爱喝水的妞妞大量饮用牛奶和果汁。</p><p>	这也是不正确的。虽然牛奶或鲜榨果汁都是较为健康的饮品，适量饮用可以补充营养，但果汁中含有大量的糖，纤维素含量却没有完整水果丰富；牛奶中钙、脂肪和蛋白质含量都很高。如果按照饮用水的量来饮用牛奶或鲜榨果汁，摄入的糖、蛋白质和脂肪会远超孩子生长所需。</p><p>	大量饮用这些饮品，不仅会影响孩子的食欲，养成不良的饮食习惯，让孩子更不喜欢喝白开水，更会造成过多的能量摄入。这些多余的能量并不能有助于孩子的生长发育，而是引起儿童肥胖、糖尿病的元凶，威胁孩子的健康。</p><p>	此外，大量饮用牛奶时，过量的蛋白质会影响钙的吸收，反而不利于孩子补钙。</p><p>	因此，牛奶、果汁以及其他的碳酸饮料、功能饮料等，都不宜给生长发育期的孩子多喝，更不能在日常生活中当作水来饮用。孩子们最健康的饮品还是水。</p><h2 class=\"ql-align-center\">	<strong style=\"color: rgb(51, 127, 229);\">误区五：吃饭时给孩子多喝水，可以助消化吗？</strong></h2><p>	妞妞妈妈认为，吃饭时多喝水可以帮助消化食物，因此有意识地给妞妞吃饭时喂大量的水。</p><p>	实际上，对于孩子来说，吃饭前先喝水，或边吃饭边大量饮水，不仅不能促进消化，反而会影响消化功能。这是由于孩子的消化功能发育不完全，大量的水会稀释胃酸，胃酸浓度降低会导致消化食物的能力下降，也会影响胃的蠕动，甚至可能诱发肠胃炎。同样，饭后大量喝水，也不利于食物的消化吸收。</p><p>	另一方面，吃饭时喝水可能导致孩子咀嚼食物不充分，唾液分泌减少，大块的食物没有和唾液充分混合，这样也不利于胃肠的消化吸收。</p><p>	因此，孩子吃饭前后和吃饭过程中，都不宜大量喝水，而是应该在饭前半小时或饭后两小时喝水。当然，如果孩子感觉口渴或者噎食时，适当饮水是可以的。</p><p>	每个孩子的健康成长都需要家长的悉心呵护，家长在育儿过程中应避免以上五个误区，培养孩子健康喝水、主动喝水的生活习惯，希望所有孩子都能快乐健康、茁壮成长！</p>','0','admin','2022-04-05 20:18:29','',NULL,NULL,'kepu-13.jpg'),(42,'孩子换牙早晚和骨龄有关吗？','1','<p>&nbsp;	孩子换牙的早晚跟骨龄有一定的相关性。但由于个体差异性比较大，一般都是用骨龄来判断孩子整个的发育成熟的情况。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;由于现在孩子吃的东西太过精细，不咀嚼，所以牙齿发育往往偏晚、偏弱一些，有的孩子连下颌都发育不太好。孩子要养成一个好的咀嚼习惯，不能狼吞虎咽。孩子吃得快，实际上未必是吃得好的表现，还是要鼓励孩子要去咀嚼。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;现在很多孩子都喜欢喝果汁，个人认为这是个不健康的习惯，因为第一在榨汁的过程中，很多人把粗纤维全扔了，第二不知不觉的就超量了，第三失去了磨牙的好机会。小孩子天天喝果汁，一个是发胖的问题，第二个痛风的问题，长期大量的喝果汁、吃果糖会干扰嘌呤的代谢，会引起高尿酸血症。现在代谢性疾病低龄化的现象很严重，所以这也是家长要科学喂养的原因，不是食物越精越好，食物吃的越多越好。</p>','0','admin','2022-04-05 20:20:38','',NULL,NULL,'kepu-14.jpg'),(43,'关于帕金森病患者的血压波动，看这一篇就够了','1','<p>	<span style=\"color: rgb(51, 51, 51);\">2021年4月11日是第25个世界帕金森病日，今年的主题是“关注帕金森病生活质量”。帕金森病患者如何早期识别自己的血压问题？患者一天中又该如何管理好自己的血压？</span></p><h2 class=\"ql-align-center\">	<span style=\"color: rgb(51, 127, 229);\">如何早期识别自己的血压问题</span></h2><h2 class=\"ql-align-center\">	<span style=\"color: rgb(51, 127, 229);\">帕金森病患者要了解的9个问题</span></h2><p>	<strong>1、帕金森病患者血压波动会有哪些表现？</strong></p><p>	帕金森病患者会出现血压波动，表现为血压过低（体位性低血压）或过高（卧位高血压）。虽然相当多的患者是无症状的，但仍然有一部分患者可以表现出明显的症状，例如站立位头晕，严重者反复出现晕厥，不仅限制活动，还可能造成外伤等恶性后果。血压过低除了容易在站立位时出现，也容易出现在餐后，特别是进食过饱或淀粉类食物过多时，称为餐后低血压。</p><p>	<strong>2、什么是体位性低血压？</strong></p><p>	体位性低血压是帕金森病常见的非运动症状之一，约有50%~70%的患者会出现该症状。与多系统萎缩相比，帕金森病患者的体位性低血压经常是无症状的，因此并不能引起患者的足够重视。然而研究表明，即便是无症状的体位性低血压，也会给患者增加跌倒风险、加重活动障碍，并且增加健康服务消耗。因此，体位性低血压是一种严重影响患者生活质量的症状。</p><p>	<strong>3、体位性低血压和神经源性体位性低血压有什么风险？</strong></p><p>	目前，前驱期帕金森病诊断标准已经把体位性低血压列为危险标志物之一。存在症状性的体位性低血压的人群，患病风险会增加3.2倍，而存在神经源性体位性低血压的人群，患病风险会增加18.5倍。由此可见，体位性低血压和神经源性体位性低血压是非常重要的疾病征象。</p><p>	<strong>4、哪些人群需要关注自己是否存在体位性低血压的风险？</strong></p><p>	（1）怀疑或诊断为伴自主神经损害的神经退行性疾病（如帕金森病、多系统萎缩、路易体病、单纯自主神经功能障碍）</p><p>	（2）曾发生不明原因跌倒或晕厥的人群</p><p>	（3）诊断为伴自主神经损害的周围神经病</p><p>	（4）70岁以上的老年人、身体状态虚弱的人群或存在多重用药的人群</p><p>	（5）出现体位性头晕或非特异性站立位症状的人群</p><p>	<strong>5、有没有办法早期自测是否患有体位性低血压？（以下任何问题如为肯定回答，都建议您进一步就诊检查）</strong></p><p>	（1）近期有无晕倒？</p><p>	（2）站立时有无头晕或头重脚轻的症状存在？</p><p>	（3）站立时有无视物障碍？</p><p>	（4）站立时有无觉得呼吸困难？</p><p>	（5）站立时有无感觉腿发软？</p><p>	（6）站立时有无感觉颈部疼痛？</p><p>	（7）上述症状是否随坐下或躺下而改善或消失？</p><p>	（8）上述症状是否在晨起或餐后加重？</p><p>	（9）近期有无跌倒？</p><p>	（10）有无其他在站立位3~5分钟内出现的症状，并随坐下或躺下而改善或消失？</p><p>	<strong>6、哪些药物会干扰体位性低血压？</strong></p><p>	由于帕金森病的药物多数有降低血压的作用，因此会带来额外的血压波动机会。以下药物具有降低血压和导致体位性低血压的副作用，患者和家属在就医前先检查一下在服的药品。如确有服用，也不要擅自作主停用，最好请相关专科医生来协助判断是否有必要停药、减量、换药还是继续服用。</p><p>	<strong>7、日常监测卧立位血压，应该怎么做？</strong></p><p>	准备的用具：电子血压计</p><p>	环境和状态：在安静的环境中空腹测量，清晨是最好的时机，当然如您在其他时间容易出现症状，也可以随时测量。着宽松衣物，便于绑袖带。</p><p>	测量步骤：</p><p>	（1）安静平卧5分钟，测量血压，记录收缩压、舒张压和心率；</p><p>	（2）不摘袖带，站立起身，原地站立1分钟，测量血压，记录同上；</p><p>	（3）继续原地站立至3分钟，再次测量血压，记录同上；</p><p>	（4）大多数时候以上步骤就足够了，但少数患者还需要延长站立时间才可能诱发出体位性低血压，此时可以继续保持原地站立至5分钟，再次测量血压，记录同上。一般最长站立至10分钟。</p><p>	注意事项：</p><p>	假如患者在站立位有明显的头晕症状出现，甚至眼前发黑，一定要及时终止测量，立即坐下或躺下，以缓解症状。</p><p>	<strong>8.医生给我开了升压药和降压药，我该怎么用？</strong></p><p>	升压药需要您在容易出现体位性低血压的时候使用，例如需要活动时或餐后。由于患者服药后可能引起血压升高，因此不建议服药后躺下休息，以免血压过高；降压药最好在睡前使用，但注意使用短效降压药。</p><p>	<strong>9.帕金森病患者合并体位性低血压该如何治疗？</strong></p><p>	血压在一天中是持续波动的，因此具体如何治疗体位性低血压需要根据原发疾病和环境因素特点进行综合考量。与其说治疗体位性低血压，不如说管理血压。</p><p>	索隆教授结合临床经验给患者朋友送上了一日血压管理建议——</p><p>	帕金森病患者的一日血压管理建议</p><p>	「早上6点」——该起床啦！</p><p>	•饮水400~500ml，可以是淡盐水或功能性饮料；</p><p>	•在起床前做一下下肢肌肉收缩的练习，例如勾脚、绷脚；</p><p>	•穿好弹力袜（可以增加15~20mmHg的血压），如果觉得弹力袜特别不舒服，也可以考虑佩戴腹带；</p><p>	•如果医生开了升压药（如米多君），起床前是最合适的服药时机。</p><p>	「早上6:30」——晨练和服药</p><p>	•洗漱活动结束后，开始服用第一顿帕金森病药物；</p><p>	•为了配合药物起效，可以适当做一些康复老师教的伸展动作，这时如果药物还没起效，建议坐位完成动作，以保证您的安全。</p><p>	「早上7:30」——早餐时间</p><p>	•吃早餐，注意不要吃太饱，也不要都吃淀粉类食物，以免加重餐后低血压。</p><p>	「早上8:00」——活动时间</p><p>	•开始工作或者上午的活动吧！</p><p>	「上午10:00」——上午休息</p><p>	•稍事休息，补充点水分，可以适当加餐。</p><p>	•忙里偷闲时可做些肌肉收缩的训练哦。</p><p>	「中午11:00」——服药</p><p>	•准备吃午饭了，午餐前一个小时服用抗帕金森病药物，如果午餐后您的血压容易偏低，可以在此时同时服用升压药物；</p><p>	•服药后别忘了记录是否有低血压的发作（例如有无头晕现象及出现时间）。</p><p>	「中午12:00」——午餐时间</p><p>	•吃午饭。和早餐时一样，注意不要吃太饱，也不要都吃淀粉类食物；</p><p>	•进餐后适当休息，可以在躺椅上靠一靠（不要完全躺平），有助于减轻餐后因血压低产生的疲劳感。</p><p>	「下午4:00-5:00」——服药和康复训练</p><p>	•准备服用第三顿帕金森病药物；</p><p>	•此时可以做一些康复训练动作，例如下肢的肌肉收缩练习。下肢力量的加强有助于体位性低血压的改善，可以降低使用升压药的频率。</p><p>	「下午6:00」——晚餐时间</p><p>	•吃晚饭。和前两顿一样，注意不要吃太饱，也不要都吃淀粉类食物。通常晚餐可以安排一些富含蛋白质类的食物，以补充一天中人体所需的蛋白质。</p><p>	•晚餐后仍然要稍事休息。</p><p>	「晚上7:00-8:00」——康复训练时间</p><p>	•可以做些康复训练。</p><p>	「晚上9:00」——服药</p><p>	•有些人要准备服用一天中的最后一顿帕金森病药物了；</p><p>	•回顾并记录今天是否发生了体位性低血压或者相关的症状。</p><p>	「晚上10:00-11:00」——准备就寝</p><p>	•避免睡前饮水；</p><p>	•如果您有夜间高血压，需酌情服用短效的降压药物；</p><p>	•弹力袜或者腹带用了一天了，是时候解放一下了；</p><p>	•如果想要泡个脚或泡个澡，一定要控制好时间，水温不要过热；</p><p>	•头部高位休息（30~45°），尽量避免平卧式。</p>','0','admin','2022-04-05 20:25:35','',NULL,NULL,'kepu-15.jpg'),(44,'花粉过敏烦恼多，普渡专家来支招','1','<p>	<span style=\"color: rgb(51, 51, 51);\">当前，全世界花粉症患病总人数预计超过5000万人，据调查显示，我国内蒙古地区过敏性鼻炎发病率为32.4%，其中18.5%由花粉过敏引起。严冬已经过去，春暖花开的日子到来了，但是不少人因为花粉过敏又开始发愁了。别担心，普渡专家来为您支招啦！想知道如何预防花粉过敏，花粉过敏又该如何治疗……快随我一同看下去吧~</span></p><p>	<strong>什么是花粉过敏？</strong></p><p>	花粉过敏即花粉症，是指具有特异性遗传素质的患者吸入致敏花粉后引发的以变应性鼻结膜炎、哮喘为主的一系列临床症状。这些症状具有明显的时间性和地区性，并且易受某些气象因素的影响。</p><p>	<strong>如何发现自己花粉过敏？</strong></p><p>	如果你有流鼻涕、打喷嚏、鼻眼痒、咳嗽、胸闷、憋气等症状，并且有明显的季节性，就要当心啦！你很可能花粉过敏。</p><p>	<strong>哪些花粉容易引起过敏？</strong></p><p>	在我国北方，春天最容易引起过敏的树木花粉，主要来自柏树、桦树、梧桐、白蜡、臭椿。</p><p><strong>&nbsp;&nbsp;&nbsp;花粉的结构和分类</strong></p><p>	一朵完整的花分为六个基本部分，分别是花梗、花托、花萼、花冠、雄蕊群和雌蕊群。其中花梗与花托相当于枝的部分，其余四个部分相当于枝上的变态叶，常合称为花部。一朵四部俱全的花称为完全花，缺少其中的任一部分则称为不完全花。</p><p>	<strong>为什么花粉过敏的人变多了？</strong></p><p>	1、局部地区植物种类不平衡，某一类或某几类植物的花粉浓度高。</p><p>	2、花粉颗粒和雾霾颗粒物一旦结合，更容易诱发和加重过敏。例如有研究表明，在雾霾环境里，桦树会释放更多的Bet v 1（桦树花粉过敏原），更容易引起人过敏。</p><p>	<strong>如何预防或避免花粉过敏？</strong></p><p>	1、出差或者换一个城市居住</p><p>	花粉过敏的北方患者，在花粉过敏季节去南方，症状就会明显减轻或者消失。由于北方的桦树和柏树特别多，在春天特别容易引起花粉过敏。</p><p>	2、戴口罩</p><p>	最好佩戴专门预防花粉过敏的口罩，如果没有，普通医用口罩也可以减轻花粉过敏症状。</p><p>	3、戴防花粉眼镜</p><p>	对于因花粉过敏引起结膜炎的患者，在外出时，可以戴防花粉眼镜来减轻过敏性结膜炎症状。</p><p>	4、室内安装新风系统或者空气过滤净化器</p><p>	花粉季节到来时，花粉过敏患者在室内需要关闭门窗以减少室内花粉浓度，如果条件允许，应该在室内安装新风系统或者空气过滤净化器。</p><p>	<strong>花粉过敏如何治疗？</strong></p><p>	1、对症治疗</p><p>	应使用抗组胺药物对症治疗过敏性鼻炎和结膜炎，对于症状比较重的患者，可以使用激素鼻喷剂治疗过敏性鼻炎。</p><p>	2、抗IgE（免疫球蛋白）治疗</p><p>	对于症状特别严重的患者，可以使用抗IgE治疗，每个月皮下注射一次即可。</p><p>	3、免疫治疗</p><p>	免疫治疗是病因治疗，特别适合同时合并秋季花粉过敏的患者，但是，一般需要持续3-5年，如果患者愿意，可以前往变态反应门诊进行相关诊断和治疗。</p>','0','admin','2022-04-05 20:27:49','',NULL,NULL,'kepu-16.jpg'),(45,'心力衰竭患者的自我管理（出入量管理）','1','<p class=\"ql-align-center\">心脏是一个血泵，它功能不好了，就带不动太多的血在身体里流动，带不动的血留在肺里或者腹部，患者就会出现气短、躺不平和浮肿等症状。</p><p><strong>&nbsp;&nbsp;&nbsp;&nbsp;我们可以改变心衰，但前提是我们自己能够了解这个疾病并且做出一些努力：</strong>心衰是一种严重的疾病，但是只要有良好的自我管理和与医生护士配合的长期随诊，心衰是可以控制的甚至痊愈的，想想生命中那些美好的事情、亲人和自己的梦想，这些都值得我们付出一些努力，去了解自己的疾病，和做一些改变，不是吗？下面我们会一步步教会您怎么进行自我管理。</p><p><br></p><p><br></p><h2 class=\"ql-align-center\"><strong style=\"color: rgb(51, 127, 229);\">心力衰竭患者居家必备物品</strong></h2><p>1 有刻度的水杯，计量每天喝的水量。</p><p>2 有刻度的量杯，为了计量每天的尿量。</p><p>3 可以显示具体数值的体重秤，为了精确计量体重。</p><p>4 电动的臂式血压计，为了测量血压和心率。</p><p>5 药盒，把每天吃的药放在药盒里，以避免忘记。</p><p>6 可以计量摄入食盐量的勺子。我们下文说的“茶匙”是指这个图片中5ml的勺子。</p><p>7 一个记录症状的笔记本。</p><p><br></p><h2 class=\"ql-align-center\"><strong style=\"color: rgb(51, 127, 229);\">心力衰竭最基本的自我管理就是出入量的管理</strong></h2><p><strong>&nbsp;&nbsp;&nbsp;&nbsp;什么叫做出入量？</strong></p><p>&nbsp;&nbsp;&nbsp;&nbsp;入量，就是我们喝的所有水分、汤等所有液体的成分，都叫做入量，包括我们吃的水果，也是入量的一部分，展示给患者出入量表格。在心衰严重的时候，我们要记24小时出入量，即从今天早上8点到第二天早上8点的入量，加起来，算出总和，是入量。</p><p>出量：主要是尿量。我们身体还有多种方式挥发水分，但是出去的水分，最重要的方式还是尿量。在心衰严重阶段，患者也需要每天记尿量，即从今天早上8点到第二天早上8点的尿量，每次尿尿之后，放在量杯中，记录一下数值，24小时的尿量加起来，算出总和，是出量。</p><p><strong>&nbsp;&nbsp;&nbsp;为什么记录出入量是心衰的基本管理？</strong></p><p>&nbsp;&nbsp;&nbsp;严格的出入量管理是控制心衰患者出现严重症状最好的方法，此外，很多改善心衰的药物，只要在患者没有浮肿、气短的时候才能发挥作用，因此为了让心衰好转，患者必须学会记录出入量。</p><p><strong>&nbsp;&nbsp;&nbsp;液体包括：</strong>水、牛奶、软饮料、酒精、冰淇淋、奶精、咖啡、茶、果汁、果味饮料、蔬菜汁、果冻、奶油、冰冻果子露、果汁雪糕、汤、棒冰、冰块。</p><p><br></p><h2 class=\"ql-align-center\"><strong style=\"color: rgb(51, 127, 229);\">怎么控制入液量？</strong></h2><p>&nbsp;&nbsp;&nbsp;&nbsp;心力衰竭有症状时，出量一般比入量要多，这靠控制入量和利尿剂来调节；无症状时，我们要尽量维持入量和出量差不多。出入量的多少，是临床医生帮助患者改善症状最重要的依据。对于轻中度患者，我们不强调绝对限水，总体原则是“不渴不喝，喝了不渴”就行了。但是对于严重心衰患者，液体摄入量限制在1.5-2.0升/天（矿泉水瓶24小时小于4瓶水），有助于减轻症状和充血。另外化验血钠低于130mmol/L的患者，24小时入液量也不应该超过2升/天。没有症状时，在不出大汗的情况下，24小时入液量在2.5升左右，就是5个矿泉水瓶子那么多的水。</p><p><br></p><h2 class=\"ql-align-center\"><strong style=\"color: rgb(51, 127, 229);\">控制水潴留的小技巧</strong></h2><p>&nbsp;&nbsp; &nbsp;1.在两餐中间食用医生允许的冰水果和蔬菜。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;2.试着选择柠檬片、酸味硬糖、口香糖在口干时刺激唾液分泌。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;3.漱口水漱口。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;4.口渴时嚼点冰块。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;5.用小杯子装饮品。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;6.请牢记2杯水就相当于增加了半斤的体重。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;7.可以用冰盒冷冻允许进食的饮品量。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;8.小口喝水，而不是大口大口。&nbsp;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;9.分配每天的饮水量，并把水储存在容器中放于冰箱内。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;10.保持房间潮湿。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;11.心衰患者不主张喝茶水养生的观点。</p><p><br></p><h2 class=\"ql-align-center\"><strong style=\"color: rgb(51, 127, 229);\">心衰的时候需要输液吗？</strong></h2><p>&nbsp;&nbsp;&nbsp;&nbsp;很多患者会认为治病就是输液，这是错误的。治病的时候需要输入一些药物，有些药物可能只能通过静脉输液的方式进入身体，这些药物包括一些抗生素、心衰用药等，但是这些药物需要的液体都很少，心衰时医生会控制输入的液量，连续不停的大瓶子输液，是不可取的。多数的心衰用药是口服的，在病情平稳期，患者是完全不需要输液的。</p><p><br></p><h2 class=\"ql-align-center\"><strong style=\"color: rgb(51, 127, 229);\">称体重是衡量身体里有没有多余的水分的最简单方法&nbsp;&nbsp;</strong></h2><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;每天量体重：记录并跟踪体重变化，应该每天晨起称量体重，使用同一个体重秤、穿相同数量的衣物。晨起第一件事就是排尿后称体重。每天记录下体重情况。如果今天早上的体重是50kg，第二天早上的体重是52kg，往往意味着体内的水分增长显著，此时就要考虑在下午的时候多加一片利尿剂，或者嘱咐老病人在下午4点左右评估一下从上午到4点的尿量，如果比往日偏少，要考虑加一片利尿剂。加用利尿剂，就要注意补钾，以免出现低血钾问题； 肾功能不全的患者补钾期间，应定期复查电解质，避免血钾过高造成的危险。有些水果如香蕉和绿叶菜含钾的量比较高，一些肾功能不全的患者食用的时候就要少吃一些。高血钾和低血钾对身体都很不利，血钾的正常范围是3.5-5.5mmol/L，我们一般希望自己的患者血钾在4mmol/L-5mmol/L左右。自己掌握不好利尿剂使用的患者，需要请医生帮助调整剂量，如果不管这种体重的增长，不需要太长时间，患者就会再次出现心力衰竭症状。因此监测每日体重的增加，对防止心衰患者的再次出现症状，非常重要。</p><p><br></p><h2 class=\"ql-align-center\"><strong style=\"color: rgb(51, 127, 229);\">心力衰竭患者的饮食忌口：低盐饮食</strong></h2><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(51, 51, 51);\">从西医角度来讲，心力衰竭患者没有必须要忌口的东西，我们希望患者营养全面，蛋、奶、肉、蔬菜和水果，都可以吃，但是有些患有糖尿病和肾功能不全的患者，高糖高脂的食品量是要少一些。在饮食方面，我们最在乎的是食盐的摄入，下面是吃盐的一般原则。盐吃多了，身体里水分就会多，血压也会有波动，因此心衰患者都建议低盐饮食。很多人心衰的原因就是因为太喜欢吃咸的东西了，这一点是需要改进。</span></p><p>	<span style=\"color: rgb(51, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;一般来讲：</span>	<span style=\"color: rgb(51, 51, 51);\">如果患者喘得很重，不能下床（我们叫重度心衰），这时候每天的限制在</span>	<span style=\"color: rgb(51, 51, 51);\">0.4</span>	<span style=\"color: rgb(51, 51, 51);\">克，实际相当于¼茶匙的食盐；</span>	<span style=\"color: rgb(51, 51, 51);\">如果患者轻度活动后就有气短（中度心衰），此时每天的每天摄入钠盐量限制在</span>	<span style=\"color: rgb(51, 51, 51);\">1</span>	<span style=\"color: rgb(51, 51, 51);\">克，实际相当于半茶匙的食盐；</span>	<span style=\"color: rgb(51, 51, 51);\">如果患者活动后没有症状（轻度心衰），此时轻度心力衰竭患者，每天摄入钠盐量限制在</span>	<span style=\"color: rgb(51, 51, 51);\">2</span>	<span style=\"color: rgb(51, 51, 51);\">克，实际相当于</span>	<span style=\"color: rgb(51, 51, 51);\">1</span>	<span style=\"color: rgb(51, 51, 51);\">茶匙的食盐。</span>	<span style=\"color: rgb(51, 51, 51);\">（注：</span>	<span style=\"color: rgb(51, 51, 51);\">¼</span>	<span style=\"color: rgb(51, 51, 51);\">茶匙</span>	<span style=\"color: rgb(51, 51, 51);\">=500mg&nbsp;</span>	<span style=\"color: rgb(51, 51, 51);\">钠；半茶匙</span>	<span style=\"color: rgb(51, 51, 51);\">=1000mg</span>	<span style=\"color: rgb(51, 51, 51);\">钠；</span>	<span style=\"color: rgb(51, 51, 51);\">3/4</span>	<span style=\"color: rgb(51, 51, 51);\">茶匙</span>	<span style=\"color: rgb(51, 51, 51);\">=1500mg</span>	<span style=\"color: rgb(51, 51, 51);\">钠；</span>	<span style=\"color: rgb(51, 51, 51);\">1</span>	<span style=\"color: rgb(51, 51, 51);\">茶匙</span>	<span style=\"color: rgb(51, 51, 51);\">=2000mg</span>	<span style=\"color: rgb(51, 51, 51);\">钠，一</span>	<span style=\"color: rgb(51, 51, 51);\">个茶匙是</span>	<span style=\"color: rgb(51, 51, 51);\">5</span>	<span style=\"color: rgb(51, 51, 51);\">毫升的小勺子，不是家里喝汤用的大羹匙！）</span></p>','0','admin','2022-04-05 20:40:31','',NULL,NULL,'kepu-17.jpg'),(46,'你的血脂正常吗？不同的人标准不一样','1','<p>		健康体检项目里头，总绕不开血脂这一项。但是实际上血脂又包含了脂蛋白、低密度脂蛋白、高密度脂蛋白、甘油三酯、总胆固醇、低密度脂蛋白胆固醇等指标。到底怎么看懂这些化验结果，如何判断指标是否正常？</p><p>	<strong style=\"color: rgb(0, 0, 0);\">什么是血脂？</strong></p><p>	<span style=\"color: rgb(0, 0, 0);\">其实，血脂就是体内脂质的总称，包括了甘油三酯、胆固醇和磷脂等。</span></p><p>	<span style=\"color: rgb(0, 0, 0);\">脂蛋白是血脂和蛋白质的结合物。因为血脂不溶于水，只有用蛋白把它包裹起来，才能在血液中运输。因此，有时候血脂会跟特定的蛋白——载脂蛋白（运载血脂的蛋白）结合在一起。脂蛋白的密度有高有低，于是就有了低密度脂蛋白、高密度脂蛋白、极低密度脂蛋白、中间密度脂蛋白等分类。不同密度的脂蛋白，它们的血脂成分比例不同，通常密度越低，含有甘油三酯就越丰富，相反，胆固醇含量的比例就越高。高密度脂蛋白中所含载脂蛋白含量非常丰富，能降低血液中的胆固醇水平。</span></p><p>	<span style=\"color: rgb(0, 0, 0);\">脂类是人体基本的营养元素之一。甘油三酯是人体储备能量和提供能量的重要源泉。一些不能进食的重病患者，还需要通过静脉输注甘油三酯来获取营养。而胆固醇在构成细胞膜、形成生理激素方面也不可或缺。我们体内的脂蛋白也有好有坏。最坏的是低密度脂蛋白胆固醇，如果含量过高，可能导致动脉粥样硬化、心绞痛、甚至心肌梗死。而高密度脂蛋白胆固醇却是一个“好人”，它能够将血管壁内的胆固醇转运到肝脏清除。</span></p><p>	</p><p>	<strong style=\"color: rgb(0, 0, 0);\">不同的人，血脂正常与否的标准不同</strong></p><p>	<span style=\"color: rgb(0, 0, 0);\">高血压和糖尿病都有比较明确的正常阈值，可高血脂却不是。同样的数值，对于某个人是正常的，对另一个人却有可能是异常的。因此，判断一个人血脂是否异常，是否需要吃药，可不是看看化验单上的箭头那么简单的事情。</span></p><p>	<span style=\"color: rgb(0, 0, 0);\">最让医生警惕的是那些曾经得过心脑血管疾病的人，他们是医学标准上的“极高危”患者。年龄大、有糖尿病史、低密度脂蛋白胆固醇比较高的人群，是医学标准上的&nbsp;“高危”人群。还有一部份人群携带心脑血管疾病的相关“危险因素”，如血压高、年纪大、长期抽烟、高密度脂蛋白胆固醇低等，也需要严格控制自己的血脂。</span></p><p>	<span style=\"color: rgb(0, 0, 0);\">医学上已经制定出来了一个风险等级表，根据危险因素的多少，评估一个人将来得心脑血管疾病风险的高低，具体分为“低危”、“中危”和“高危”三个级别，危险因素越多，危险性越大，就越需要严格控制血脂。</span></p><p>	<span style=\"color: rgb(0, 0, 0);\">所以，医生拿到化验单后，还要通过查体、询问病史等操作，来判断心血管疾病的风险等级，才能得出一个人血脂的合理目标水平，然后对照化验单上的数值，才能判断血脂是否“正常”。</span></p><p>	</p><p>	<strong style=\"color: rgb(0, 0, 0);\">血脂不正常怎么办？</strong></p><p>	<span style=\"color: rgb(0, 0, 0);\">体内的血脂水平主要受遗传、年龄和性别的影响。通过饮食摄入的胆固醇只占体内胆固醇的三分之一，其余三分之二都是肝脏自己制造出来的，因此，只靠“管住嘴”也不能充分降低体内的胆固醇水平。健康饮食、规律运动、减轻体重和戒绝烟草是降血脂的基础。假如改变生活方式的非药物治疗法仍然不能让血脂达标，那就需要在医生的指导下适量用药。</span></p>','0','admin','2022-04-05 20:42:47','',NULL,NULL,'kepu-18.jpg'),(47,'疫情之下 | 如何预防及处理“勤洗手”造成的皮肤问题','1','<p>&nbsp;近日，一张关于感染科护士双手照片受到了大家的关注，奋战在抗疫一线的年轻护士，由于长期接触手消毒凝胶以及频繁洗手，双手皮肤受损，出现干燥、皲裂和出血。最近大家响应“勤洗手，戴口罩，不聚集”的号召，无论是一线的医护人员还是普通人，洗手的频率都大大增加。但过于频繁的清洁和消毒会对皮肤屏障造成破坏，导致皮肤干燥进而引发皮炎或皮肤感染。因此，在保持手部清洁、防止接触传播的同时，避免频繁清洁对皮肤屏障的损害同样十分重要。普渡健康医院医师将为您科普“勤洗手”背后的“护手”秘籍。</p><p><br></p><h2 class=\"ql-align-center\"><span style=\"color: rgb(51, 127, 229);\">洗手过度为何会伤手？</span></h2><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;皮肤的最外层是由角质细胞形成的角质层，细胞间脂质填充于角质细胞之间，就如同“泥浆”与“砖”，形成砖墙样结构，角质层外还有一层水脂膜，三者构成了皮肤的物理屏障。物理屏障如同守护皮肤健康的“城墙”，防止皮肤内水分丢失和外界水分及有害物的渗入，以免皮肤失水干燥或引起相关皮肤疾病。寒冷干燥的环境、物理及化学刺激、过度清洁皮肤、护肤品使用不当等因素都会导致皮肤屏障受损[1]。</p><h2 class=\"ql-align-center\"><span style=\"color: rgb(51, 127, 229);\">如何预防勤洗手导致的皮肤损伤？</span></h2><p>1、明确洗手的节点，避免过度清洁</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于普通人而言，我们应在饭前便后、接触污染物之后、触摸眼口鼻之前洗手；若从外回家，需要在取下口罩前后洗手。每天洗手次数一般不要超过10次[1]。</p><p><br></p><p>2、选择合适的清洁产品</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;皮肤清洁剂按化学性质可以分为皂类清洁剂与合成型清洁剂。皂类清洁剂就是我们通常所说的肥皂，成分为碱性，去污力强，但频繁使用容易破坏皮肤表面皮脂膜，升高皮肤pH值，对皮肤有一定的刺激作用。可以选用添加了保湿成分或含甘油的改良皂类、或是合成型清洁剂如较温和的洗手液等，对皮肤的刺激较低。需要注意的是，仅在可能接触到病原微生物或准备进行无菌操作时才需使用含有消毒杀菌功效的洗手液，在居家生活的情况下，可不必使用[2]。经常使用含有酒精的手消毒剂，皮肤会因脱脂而变得干燥、粗糙。</p><p><br></p><p>3、避免刺激性消毒产品直接接触皮肤</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;除了过度清洁对皮肤带来的损害，我们使用的一些消毒产品也具有皮肤刺激性。比如常用的84消毒液，它的主要成分是次氯酸钠，具有腐蚀性，如果直接接触皮肤会造成接触性皮炎，出现红肿、疼痛等症状。在使用这类有刺激性的消毒产品时，我们应佩戴橡胶手套，避免直接接触；如果皮肤不慎接触，应立即用大量清水清洗。</p><p><br></p><p>4、洗手后使用护肤产品</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果条件允许，在每次清洁后应涂抹护手霜。甘油、尿素等成分的护手霜可以从真皮及空气中吸收水分，补充角质层水分的丢失；凡士林可以在皮肤表面形成油膜，减少皮肤水分蒸发[3,4]；神经酰胺是皮肤中的重要脂质、透明质酸是人体的基质成分之一，有助于维持皮肤屏障功能[5]。选择含有以上这些成分的润肤剂可提高皮肤的保湿能力，帮助修复皮肤屏障。</p><h2 class=\"ql-align-center\"><span style=\"color: rgb(51, 127, 229);\">手部皮肤受损后怎么办？</span></h2><p>1、若手部出现脱屑、瘙痒、皲裂等症状，表明皮肤失水干燥，皮肤屏障受损，加强润肤保湿剂的使用是治疗的基础，同时避免使用刺激性清洁剂继续刺激受损的皮肤。对于皲裂者，可使用含有尿素成分的护肤品。</p><p><br></p><p>2、若出现原有皮肤病加重，如手湿疹等，出现了红斑、瘙痒、渗出等，在使用润肤剂的同时，可加用外用糖皮质激素软膏。若渗出明显，可以使用冷湿敷，也可以外涂氧化锌软膏[6]。</p><p><br></p><p>3、接触性皮炎发生在特殊体质人群，主要表现为在接触消毒剂的部位出现红、肿、瘙痒，甚至丘疹、水疱等症状。若出现这类症状，首先应避免再次使用可疑消毒剂，更换为其它非过敏产品。轻症可在3-5天内自行缓解；若皮疹严重，可外用糖皮质激素软膏，口服第二代抗组胺药 [7] 。</p><p><br></p><p>4、若症状无缓解，建议及时到医院就诊，在皮肤科医生的指导下进行治疗。</p>','0','admin','2022-04-05 20:45:49','',NULL,NULL,'kepu-19.jpg'),(48,'补钙别急着吃钙片，一定要避开这几个坑','1','<p>&nbsp;春暖花开，万物复苏，“猫冬”过后的孩子们开始雀跃起来。一年之计在于春，春天是万物蓬勃生长的季节，很多家长常问：要不要给孩子补钙？如何补钙？</p><p><br></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一般来说，传统食物中缺乏足够的钙，并且实际上仅仅饮食中含充分的钙是不够的，被吸收入血液的钙才能发挥足够的价值。</p><h2 class=\"ql-align-center\"><span style=\"color: rgb(51, 127, 229);\">妨碍钙吸收的因素</span></h2><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1、体内缺乏维生素D。维生素D可以促进钙在体内的吸收，而体内的维生素D主要是通过皮肤日晒转化而成的，现在大家出不了门晒不了太阳就没有办法合成有效的维生素D。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2、脂肪过多形成钙皂，溶解体内的钙质随粪便排出。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3、食物中钙磷比例过多。最适合的钙磷比例为1:1.3或者1:1.5，如果食物中含磷过多，会形成磷酸盐排出体外。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;4、蔬菜里的草酸、膳食纤维还有谷物中的植酸，易与钙形成不溶解的化合物，不能被机体吸收利用。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;5、长期服用某些药物，如苯妥英钠四环素等等，这些都会影响到钙的吸收。</p><h2 class=\"ql-align-center\"><span style=\"color: rgb(51, 127, 229);\">药补不如食补&nbsp;哪些食物含钙多？</span></h2><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;补钙是必须的，但并不强调必须要通过吃钙片来补钙。实际上，药补不如食补，含钙比较多的食物有：牛奶，奶酪，鸡蛋，豆制品，绿菜叶，海带，紫菜，虾皮，芝麻酱，鱼松等等。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我们完全可以通过食补来缓解缺钙的状态，但是食物中所含的钙只有10%~30%能被人体吸收，所以务必要在饮食中消除对钙吸收的影响。</p><h2 class=\"ql-align-center\"><span style=\"color: rgb(51, 127, 229);\">通过饮食补钙的注意事项</span></h2><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1、膳食指南推荐我们每天摄入奶和奶制品300克。空腹喝奶是促进钙吸收的比较好的方式，不过有的人乳糖不耐，喝牛奶会腹胀腹泻，可以考虑喝酸奶或者吃奶酪制品。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2、谷类中的植酸，某些蔬菜中的草酸、膳食纤维能影响钙的吸收和利用，要设法减少其含量，比如面粉发酵可以分解植酸；含草酸的蔬菜焯过以后，可以除去其中干扰钙吸收的因素等。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;另外，维生素D 可以促进钙在人体内的吸收，我们可以通过晒太阳激发人体自身合成维生素D。</p><h2 class=\"ql-align-center\"><span style=\"color: rgb(51, 127, 229);\">日常如何补充维生素D</span></h2><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1、鼓励大家在天气晴好时适当地进行户外锻炼，至少15分钟，这对孩子的发育也有很大好处。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2、 6 个月以下的宝宝，因为皮肤过于娇嫩，不推荐晒太阳作为补充维生素D 的首选，优先给宝宝用单纯的维生素D滴剂。</p>','0','admin','2022-04-05 20:47:25','',NULL,NULL,'kepu-20.jpg'),(49,'关于申报2021年山东中西医结合科学技术奖的通知','2','<p>	各科室：</p><p>	首届山东中西医结合科学技术奖推荐、评审和奖励工作的有关事宜通知如下：</p><p>	山东中西医结合科学技术奖的评选内容、标准，按照《山东中西医结合科学技术奖励办法》的规定执行。</p><p>	<strong>一、奖项设置</strong></p><p>	山东中西医结合科学技术奖包括基础研究奖、临床研究奖和医学科普奖。奖励项目分三个等级，分别授予一、二、三等奖，其中医学科普奖不分等级，奖励周期为每年一次。将从获得山东中西医结合科学技术奖的项目中择优推荐中国中西医结合学会科学技术奖。</p><p>	<strong>二、申报范围</strong></p><p>	受理2017年1月1日至2020年12月31日鉴定或验收的中西医结合科技成果，科普作品（暂只评科普著作）应是2011年以后（含2011年）公开出版发行两年以上的作品。</p><p>	<strong>三、申报要求</strong></p><p>	（一）三级甲等医院、省（委）直单位、高等医学院校及附院和大企业、部队卫生处（部）单位申报项目需在核心期刊发表论文3篇以上（含3篇），与申报成果必须具有相关性。参评项目必须通过有科技成果管理权限的主管部门组织的科技成果鉴定或验收，属应用研究的要经过推广应用获得一定的社会效益和经济效益，并持有效益证明，在国外、省外完成的应明确产权归属。</p><p>	（二）参评项目不得同时向其他同级、同类奖励项目进行申报。属重大项目时，应当包括子项目，并写明其中的某子项目申报、获奖情况。</p><p>	（三）没有知识产权以及有关完成单位、完成人员等方面的争议，两个以上项目整合申报的，需所有申报单位加盖公章，所有项目参与人员均需要在知情同意书上签字。</p><p>	（四）科学技术项目应经过由政府有关部门确定的科技项目查新咨询单位查新检索，并由其出具查新咨询报告书。</p><p>	（五）限制申报要求：</p><p>	1.属于下列情况之一者，不得申报：</p><p>	（1）凡涉及国防、国家安全领域的保密项目和已获得国家级、省部级奖励的项目；</p><p>	（2）凡存在知识产权以及完成单位、完成人员等有争议未得到解决的；</p><p>	（3）已获得阶段性成果奖或子项奖科技成果，申报时未如实注明者；</p><p>	2.每个完成人最多可同时参与2个项目的申报，第一完成人限报1项。</p><p>	<strong>四、申报时间</strong></p><p>	申报项目请于2021年7月30日前将纸质书面材料<strong>报送</strong>至科研科，电子版书面材料发送邮箱tyfykyk@163.com，逾期不予受理。</p><p>	<strong>五、申报材料</strong></p><p>	（一）所有申报项目必须填写《2021年山东中西医结合科学技术奖推荐书》。</p><p>	（二）附件包括《鉴定证书》或《验收证书》、检索查新报告、效益与应用证明、必要的文件或证书（如《专利证书》、《新药证书》、批准生产文件等）、发表的论文或著作（复印件）。其中：《鉴定证书》或《验收证书》等完成人员页加盖单位公章；效益或应用证明应是应用一年以上的证明。</p><p>	（三）申报材料一式二份，《推荐书》与其附件等合订成册，应打印或复印，卷面清楚，采用A4（高297毫米，宽210毫米）纸型竖装，双面印刷，左边为装订边，正文内容字体不得小于5号。其中有一套为原件（原件系指公章为原印模，签名为原笔迹），并在首页右上角标明“原件”字样。</p>','0','admin','2022-04-15 22:57:48','',NULL,NULL,''),(50,'关于山东省中医药科技项目结题的通知','2','<p>	<strong>一、结题范围</strong></p><p>	省卫健委组织立项的、已完成项目任务书约定的研究内容和考核指标，需要在规定时限内办理结题的山东省中医药科技项目。</p><p>	<strong>二、结题要求</strong></p><p>	提交结题申请的项目须达到以下要求：</p><p>	（一）完成项目合同书约定的研究内容及考核指标；</p><p>	（二）项目承担单位、主管部门审核通过；</p><p>	（三）在规定时限内提交结题报告。</p><p>	存在下列情况之一者，不予办理结题：</p><p>	（一）研究过程或结果违背科研诚信和医学伦理规范的；</p><p>	（二）完成任务不到 85%的；</p><p>	（三）提供的资料、数据不真实的；</p><p>	（四）擅自变更考核目标、研究内容的；</p><p>	（五）存在知识产权纠纷的；</p><p>	（六）项目延期一年仍未完成任务，事先未得到审批的；</p><p>	（七）经费使用中存在严重问题的。</p><p>	<strong>三、结题材料</strong></p><p>	结题材料包括以下内容：</p><p>	（一）项目结题申请书（附件1）；</p><p>	（二）项目变更申请表（附件2；根据项目实际情况确定是否提供；如有人员变更，一般不超过3人/次，如无特殊原因，原则上前3位人员不允许变动）；</p><p>	（三）研究工作报告（概述项目实施过程、取得的主要成果、创新点及不足等）；</p><p>	（四）研究技术报告（包括中文摘要、关键词、研究背景、研究方法、结果与分析、主要结论/讨论、参考文献、附录等）；</p><p>	（五）实验动物合格证明（根据项目实际情况确定是否提供）；</p><p>	（六）研究成果，包括：发表论文、出版著作、授权专利、新药证书等（复印件）；</p><p>	（七）项目任务书（复印件）；</p><p>	（八）医学伦理证明或知情同意书等其他辅证材料。</p><p>	<strong>四、结题程序</strong></p><p>	（一）提交结题材料。结题材料（一式两份，左侧装订，胶装）由项目承担单位、主管部门审核汇总后，由各市卫生健康委填写山东省中医药科技项目结题汇总表（附件 3）集中报送项目管理办公室。委属单位、省属卫生健康事业单位和国家卫生健康委驻鲁医疗机构的项目经单位汇总后集中报送。受理时间截至2021年7月27日。</p><p>	（二）结题审核。我委组织对申请结题的项目进行审核。符合结题要求的，予以结题，并进行公布。不符合结题要求的，退回修改完善后，重新提交。</p><p>	（三）延期结题。确需延期结题的项目，由项目承担单位提出申请，说明延期原因及时间，经主管部门审核后报我委审批，延期时间原则上不超过一年。</p><p>	<strong>五、其他事项</strong></p><p>	（一）本次项目结题主要针对2017年立项课题，2017年以后立项课题我委集中受理时间为每季度第一个月上旬。</p><p>	（二）2017年度以后立项、已完成研究任务的项目，可随本次结题按照要求提交结题材料。</p><p>	<strong>六、加强科研诚信建设</strong></p><p>	项目负责人应按照国家卫生健康委等部门印发的《医学科研诚信和相关行为规范》《涉及人的生物医学研究伦理审查办法》等相关规定，履行诚信承诺，如期完成合同书规定的各项任务。项目承担单位要加强科研诚信评价体系建设和科研诚信教育，严格落实科研诚信管理主体责任，在项目结题过程中对科研诚信、医学伦理、履约情况等方面进行严格管理。主管部门要对结题申请材料进行严格审核把关，确保结题质量。</p><p>	项目管理办公室联系电话：0531-68616726、0531-68616802</p><p>	地址：济南市经十路16369号4号楼107室</p>','0','admin','2022-04-15 22:59:40','',NULL,NULL,''),(51,'普渡健康医院2021年公开招聘工作人员简章','2','<p>		<span style=\"color: rgb(51, 51, 51);\">	一、医院简介</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	</span>普渡健康医院<span style=\"color: rgb(51, 51, 51);\">坐落在雄伟壮丽的泰山脚下，是一所专业设置齐全、技术力量雄厚、医疗设备先进、服务质量优良，集医疗、教学、科研、急诊急救、预防保健和康复于一体的三级甲等综合性医院。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	目前，医院建筑面积	12.5万平方米，开放床位1900张，在职职工2000余人，其中副高级以上职称人员276人，享受国务院政府特殊津贴4人，泰山学者4人，二级教授4人，省级中青年学术骨干、重点科技人才3人，齐鲁卫生与健康领军人才2人，博士、硕士研究生导师134人，博士后导师33人、博士、硕士研究生667人。设有46个临床科室、14个医技科室。17个省级临床重点专科及建设单位、精品特色专科、医药卫生重点学科、重点实验室，25个市级临床重点专科、医药卫生重点学科、医学特色专科。拥有内科学、外科学、妇产科学、儿科学、老年医学、急诊医学、医学影像与核医学等16个硕士学位授权点。拥有GE产3.0T核磁共振、SPECT/CT、512层螺旋CT，飞利浦FD20平板DSA，医科达 Infinity四维影像引导直线加速器等万元以上先进医疗设备1700余台套,为打造高水平医疗服务提供了技术保障。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	多年来，医院不断深化改革，细化管理，狠抓内涵建设，努力为群众打造优质、高效、方便、价廉的医疗服务，受到社会广泛赞誉。先后荣获全国百家优秀爱婴医院、全国大学生临床技能竞赛一等奖、全国	“抗震救灾 重建家园”工人先锋号、全省卫生系统文明单位、山东省职业道德建设先进单位、泰安市文明单位等荣誉称号300余项。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	二、招聘岗位和人数</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	根据省委组织部、省人力资源社会保障厅《关于做好	2021年事业单位公开招聘工作人员有关问题的通知》（鲁人社字〔2020〕155号）和 中共山东省委组织部、山东省人力资源和社会保障厅《关于印发2021年度省属事业单位公开招聘工作实施方案的通知》（鲁人社发〔2021〕7号）要求，结合工作需要，山东第一医科大学第二附属医院拟面向社会公开招聘工作人员71人。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	三、招聘条件</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（一）基本条件</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	1、具有中华人民共和国国籍。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	2、遵守中华人民共和国宪法和法律。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	3、身体健康。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	4、报考初级岗位年龄在40周岁以下（1980年7月13日以后出生）,其中护理岗位年龄在30周岁以下（1990年7月13日以后出生）。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（二）岗位条件</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	1、具有符合招聘岗位要求的相关专业学历、学位及其他基本条件。详见《省属事业单位公开招聘工作人员岗位汇总表》。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	2、应届毕业生须在2021年9月30日前取得国家承认的学历、学位及相关证书，否则取消录取资格。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	国家规定择业期内（二年）未落实过工作单位的高校毕业生，以及在国（境）外教学科研机构学习，与国（境）内高校应届毕业生同期毕业的留学回国人员（含择业期内未落实过工作单位的），可以报考应届毕业生岗位。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（三）曾受过刑事处罚、曾被开除公职、开除党籍的人员，在读全日制普通高校非应届毕业生、现役军人，以及法律规定不得聘用的其他情形的人员不得应聘，在读全日制普通高校应届毕业生不能用已取得的学历学位作为条件应聘。应聘人员不能报考与本人有应回避亲属关系的岗位。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	四、招聘程序</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（一）发布招聘信息</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	通过山东省人力资源社会保障厅网站（	Http://hrss.shandong.gov.cn/）山东第一医科大学（山东省医学科学院）官网（http://www.sdfmu.edu.cn/）和</span>普渡健康医院<span style=\"color: rgb(51, 51, 51);\">官方网站（http://sah.sdfmu.edu.cn/）面向社会发布招聘信息。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（二）报名与资格审查</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	1、报名</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	报名方式	:采取现场报名的方式。应聘人员于招聘简章发布之日起7个工作日内携带所需材料及电子版直接到</span>普渡健康医院<span style=\"color: rgb(51, 51, 51);\">人事科报名，我院安排专人负责资格审查。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	报名时间：自招聘简章发布之日起	7个工作日内（上午8:30-11:30，下午2:00-5:00，节假日除外）</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	报名材料包括：</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（	1）国家承认的学历和学位证书、专业技术职称证书、执业资格证书、身份证原件及复印件；海外留学人员应聘的必须提供经教育部留学服务中心出具的《国外学历学位认证书》原件及复印件，对暂未取得学位认证的人员，允许承诺后报名考试，并于2021年9月30日前取得。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（	2）全日制普通高校应届毕业生应聘的，提交就业推荐表及复印件，并能够正常毕业。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（	3）在职人员应聘的，提交有用人权限部门或单位出具的同意应聘介绍信，对按时出具同意应聘介绍信确有困难的在职人员，可在考察或体检时提供。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（	4）报名登记表中所填写的外语等级证书及发表的论文、科研成果、获奖等材料的原件及复印件。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（	5）有专业方向要求的，提供由培养学校出具的专业方向证明。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（	6）有本人签名的《山东第一医科大学第二附属医院公开招聘报名登记表》（PDF格式及纸质版）、《应聘人员诚信承诺书》（PDF格式及纸质版）、《山东第一医科大学第二附属医院公开招聘报名信息汇总表》（.xls格式）、1寸近期同底版免冠照片2张。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	现场提交所有材料原件及复印件。所有材料的电子版按上述顺序放入以	“报名岗位+毕业院校+姓名”命名的文件夹中，现场提交。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	报名结束后，对应聘人数达不到规定比例的招聘岗位，计划招聘	1人的，取消招聘岗位；计划招聘2人以上的，按规定比例相应核减招聘人数。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	2、资格审查：对应聘人员的资格审查工作，贯穿招聘工作的全过程。应聘人员需如实填写、提交相关个人信息资料。应聘人员提供的相关材料信息如有不实，一经发现取消资格。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（三）考试</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	考试分为笔试和面试。根据疫情防控的有关要求，组织笔试、面试工作。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	1、笔试</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	笔试内容为应聘岗位所需专业基础知识，笔试采用百分制计算成绩，总分为	100分。笔试结束后及时公布笔试成绩。根据招聘计划和招聘岗位确定笔试合格分数线，按笔试成绩由高到低分按1:3的比例依次确定面试人选；笔试合格人数达不到规定比例的，按实有合格人数确定。笔试合格名单及面试相关工作安排将在山东第一医科大学第二附属医院网站公布。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	笔试时间和地点以医院网站公布为准，不再通知本人，请关注医院网站。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	2、面试</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	面试采取结构化面试方式进行。主要考察应聘人员的逻辑思维能力、应变表达能力、举止仪表以及与岗位相适应的专业知识等方面的素质和能力。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	面试总分为	100分，设定面试合格分数线70分，达到面试合格分数线的方可进入考察范围。面试成绩在面试结束后当场公布。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	面试时间和地点以医院网站公布为准，不再通知本人，请关注医院网站。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	面试结束后，按笔试成绩	50%和面试成绩50%的比例百分制合成考试总成绩。笔试成绩、面试成绩、总成绩均计算到小数点后两位数，尾数四舍五入。同一招聘岗位应聘人员出现总成绩并列的，则按笔试成绩由高分到低分确定进入考察范围人选。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（四）考察体检</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	按照招聘岗位，根据应聘人员的总成绩，由高分到低分按照	1:1.5的比例确定进入考察范围人选，并依次组织考察。对考察合格人员，按招聘人数1:1的比例确定进入体检范围人选。由山东第一医科大学第二附属医院成立考察体检工作小组，具体负责考察体检工作。对考察体检不合格人员造成的空缺，可从其他进入同一岗位考察范围的人员中依次等额递补。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	考察根据岗位条件采用多种方式进行，主要考察思想政治表现、道德品质、业务能力和工作实绩等方面情况，并对应聘人员是否符合规定的岗位资格条件、提供的相关材料是否真实准确等进行复审。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	体检按规定组织，标准参照《关于修订	&lt;公务员录用体检通用标准&gt;及&lt;公务员录用体检操作手册(试行)&gt;有关内容的通知》（人社部发[2016]140号）执行。国家另有规定的从其规定。对按规定需要复检的，复检只能进行1次，结果以复检结论为准。进入考察范围未按规定时间、地点参加体检的，视为自动放弃。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	（五）公示聘用</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	对考试、考察、体检合格的拟聘用人员，按规定在山东第一医科大学（山东省医学科学院）网站进行公示，公示期为	7个工作日。公示无异议的，报省人力资源社会保障厅审核备案。符合聘用条件的，发放《事业单位招聘人员备案通知书》，凭《事业单位招聘人员备案通知书》办理相关手续，聘用单位和受聘人员按规定签订聘用合同，确立人事关系。受聘人员按规定实行试用期制度，期满合格的正式聘用，不合格的解除聘用合同。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	五、待遇</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	享受国家规定的待遇和医院在职职工福利待遇。</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	六、联系人及联系方式</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	联系人：常老师</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	联系电话：	0538-6236412</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	地址：</span>普渡健康医院<span style=\"color: rgb(51, 51, 51);\">门诊楼	706人事科</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	七、其他</span></p><p>		<span style=\"color: rgb(51, 51, 51);\">	未尽事宜，按照省委组织部、省人力资源和社会保障厅《关于做好	2021年事业单位公开招聘工作人员有关问题的通知》（鲁人社字〔2020〕155号）的相关规定执行。</span></p>','0','admin','2022-04-15 23:02:00','',NULL,NULL,''),(52,'关于泰安市科技发展计划项目结题的通知','2','<p>	各科室：</p><p>	根据泰安市科技局的通知，现集中组织2018年以来市科技发展计划项目结题，现将有关要求和程序通知如下：</p><p>	一、材料要求</p><p>	1．纸质结题材料按照下列顺序装订成册:（1）泰安市科技计划项目验收意见表；（2）研究成果的附件材料复印件，包括：已发表论文、申请或授权专利、科研奖励证书、专著等。（注：稿件录用通知、书稿以及未标注成果不能作为结题依据）。</p><p>	2．电子文档要求:以上纸质材料的电子文档一套，电子文档应建立个人压缩文件夹，命名为“项目编号+承担单位+项目负责人”（PDF格式）。</p><p>	二、时间要求</p><p>	课题主持人于2021年5月30日前，填写2021年泰安市计划结题一览表发至tyfykyk@163.com；于6月18日将纸质材料一式两份交至科研科，电子文档（PDF格式）发至tyfykyk@163.com。逾期不予接收和办理。</p><p>	三、其他要求</p><p>	参照《泰安市科技创新发展项目管理办法》（泰科发〔2020〕7号）第十九条、二十条办理事项调整及延期申请，由科研科批准存档，附验收材料内；依据第二十八条有关规定，对到期拒不验收的项目，取消1-3年项目负责人申报资格。</p>','0','admin','2022-04-15 23:03:14','',NULL,NULL,''),(53,'医疗保障基金使用监督管理条例','2','<p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第一章　总则</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第一条　为了加强医疗保障基金使用监督管理，保障基金安全，促进基金有效使用，维护公民医疗保障合法权益，根据《中华人民共和国社会保险法》和其他有关法律规定，制定本条例。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二条　本条例适用于中华人民共和国境内基本医疗保险（含生育保险）基金、医疗救助基金等医疗保障基金使用及其监督管理。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第三条　医疗保障基金使用坚持以人民健康为中心，保障水平与经济社会发展水平相适应，遵循合法、安全、公开、便民的原则。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第四条　医疗保障基金使用监督管理实行政府监管、社会监督、行业自律和个人守信相结合。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第五条　县级以上人民政府应当加强对医疗保障基金使用监督管理工作的领导，建立健全医疗保障基金使用监督管理机制和基金监督管理执法体制，加强医疗保障基金使用监督管理能力建设，为医疗保障基金使用监督管理工作提供保障。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第六条　国务院医疗保障行政部门主管全国的医疗保障基金使用监督管理工作。国务院其他有关部门在各自职责范围内负责有关的医疗保障基金使用监督管理工作。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">县级以上地方人民政府医疗保障行政部门负责本行政区域的医疗保障基金使用监督管理工作。县级以上地方人民政府其他有关部门在各自职责范围内负责有关的医疗保障基金使用监督管理工作。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第七条　国家鼓励和支持新闻媒体开展医疗保障法律、法规和医疗保障知识的公益宣传，并对医疗保障基金使用行为进行舆论监督。有关医疗保障的宣传报道应当真实、公正。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">县级以上人民政府及其医疗保障等行政部门应当通过书面征求意见、召开座谈会等方式，听取人大代表、政协委员、参保人员代表等对医疗保障基金使用的意见，畅通社会监督渠道，鼓励和支持社会各方面参与对医疗保障基金使用的监督。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">医疗机构、药品经营单位（以下统称医药机构）等单位和医药卫生行业协会应当加强行业自律，规范医药服务行为，促进行业规范和自我约束，引导依法、合理使用医疗保障基金。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二章　基金使用</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第八条　医疗保障基金使用应当符合国家规定的支付范围。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">医疗保障基金支付范围由国务院医疗保障行政部门依法组织制定。省、自治区、直辖市人民政府按照国家规定的权限和程序，补充制定本行政区域内医疗保障基金支付的具体项目和标准，并报国务院医疗保障行政部门备案。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第九条　国家建立健全全国统一的医疗保障经办管理体系，提供标准化、规范化的医疗保障经办服务，实现省、市、县、乡镇（街道）、村（社区）全覆盖。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第十条　医疗保障经办机构应当建立健全业务、财务、安全和风险管理制度，做好服务协议管理、费用监控、基金拨付、待遇审核及支付等工作，并定期向社会公开医疗保障基金的收入、支出、结余等情况，接受社会监督。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第十一条　医疗保障经办机构应当与定点医药机构建立集体谈判协商机制，合理确定定点医药机构的医疗保障基金预算金额和拨付时限，并根据保障公众健康需求和管理服务的需要，与定点医药机构协商签订服务协议，规范医药服务行为，明确违反服务协议的行为及其责任。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">医疗保障经办机构应当及时向社会公布签订服务协议的定点医药机构名单。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">医疗保障行政部门应当加强对服务协议订立、履行等情况的监督。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第十二条　医疗保障经办机构应当按照服务协议的约定，及时结算和拨付医疗保障基金。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">定点医药机构应当按照规定提供医药服务，提高服务质量，合理使用医疗保障基金，维护公民健康权益。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第十三条　定点医药机构违反服务协议的，医疗保障经办机构可以督促其履行服务协议，按照服务协议约定暂停或者不予拨付费用、追回违规费用、中止相关责任人员或者所在部门涉及医疗保障基金使用的医药服务，直至解除服务协议；定点医药机构及其相关责任人员有权进行陈述、申辩。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">医疗保障经办机构违反服务协议的，定点医药机构有权要求纠正或者提请医疗保障行政部门协调处理、督促整改，也可以依法申请行政复议或者提起行政诉讼。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第十四条　定点医药机构应当建立医疗保障基金使用内部管理制度，由专门机构或者人员负责医疗保障基金使用管理工作，建立健全考核评价体系。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">定点医药机构应当组织开展医疗保障基金相关制度、政策的培训，定期检查本单位医疗保障基金使用情况，及时纠正医疗保障基金使用不规范的行为。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第十五条　定点医药机构及其工作人员应当执行实名就医和购药管理规定，核验参保人员医疗保障凭证，按照诊疗规范提供合理、必要的医药服务，向参保人员如实出具费用单据和相关资料，不得分解住院、挂床住院，不得违反诊疗规范过度诊疗、过度检查、分解处方、超量开药、重复开药，不得重复收费、超标准收费、分解项目收费，不得串换药品、医用耗材、诊疗项目和服务设施，不得诱导、协助他人冒名或者虚假就医、购药。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">定点医药机构应当确保医疗保障基金支付的费用符合规定的支付范围；除急诊、抢救等特殊情形外，提供医疗保障基金支付范围以外的医药服务的，应当经参保人员或者其近亲属、监护人同意。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第十六条　定点医药机构应当按照规定保管财务账目、会计凭证、处方、病历、治疗检查记录、费用明细、药品和医用耗材出入库记录等资料，及时通过医疗保障信息系统全面准确传送医疗保障基金使用有关数据，向医疗保障行政部门报告医疗保障基金使用监督管理所需信息，向社会公开医药费用、费用结构等信息，接受社会监督。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第十七条　参保人员应当持本人医疗保障凭证就医、购药，并主动出示接受查验。参保人员有权要求定点医药机构如实出具费用单据和相关资料。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">参保人员应当妥善保管本人医疗保障凭证，防止他人冒名使用。因特殊原因需要委托他人代为购药的，应当提供委托人和受托人的身份证明。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">参保人员应当按照规定享受医疗保障待遇，不得重复享受。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">参保人员有权要求医疗保障经办机构提供医疗保障咨询服务，对医疗保障基金的使用提出改进建议。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第十八条　在医疗保障基金使用过程中，医疗保障等行政部门、医疗保障经办机构、定点医药机构及其工作人员不得收受贿赂或者取得其他非法收入。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第十九条　参保人员不得利用其享受医疗保障待遇的机会转卖药品，接受返还现金、实物或者获得其他非法利益。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">定点医药机构不得为参保人员利用其享受医疗保障待遇的机会转卖药品，接受返还现金、实物或者获得其他非法利益提供便利。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二十条　医疗保障经办机构、定点医药机构等单位及其工作人员和参保人员等人员不得通过伪造、变造、隐匿、涂改、销毁医学文书、医学证明、会计凭证、电子信息等有关资料，或者虚构医药服务项目等方式，骗取医疗保障基金。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二十一条　医疗保障基金专款专用，任何组织和个人不得侵占或者挪用。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第三章　监督管理</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二十二条　医疗保障、卫生健康、中医药、市场监督管理、财政、审计、公安等部门应当分工协作、相互配合，建立沟通协调、案件移送等机制，共同做好医疗保障基金使用监督管理工作。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">医疗保障行政部门应当加强对纳入医疗保障基金支付范围的医疗服务行为和医疗费用的监督，规范医疗保障经办业务，依法查处违法使用医疗保障基金的行为。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二十三条　国务院医疗保障行政部门负责制定服务协议管理办法，规范、简化、优化医药机构定点申请、专业评估、协商谈判程序，制作并定期修订服务协议范本。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">国务院医疗保障行政部门制定服务协议管理办法，应当听取有关部门、医药机构、行业协会、社会公众、专家等方面意见。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二十四条　医疗保障行政部门应当加强与有关部门的信息交换和共享，创新监督管理方式，推广使用信息技术，建立全国统一、高效、兼容、便捷、安全的医疗保障信息系统，实施大数据实时动态智能监控，并加强共享数据使用全过程管理，确保共享数据安全。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二十五条　医疗保障行政部门应当根据医疗保障基金风险评估、举报投诉线索、医疗保障数据监控等因素，确定检查重点，组织开展专项检查。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二十六条　医疗保障行政部门可以会同卫生健康、中医药、市场监督管理、财政、公安等部门开展联合检查。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">对跨区域的医疗保障基金使用行为，由共同的上一级医疗保障行政部门指定的医疗保障行政部门检查。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二十七条　医疗保障行政部门实施监督检查，可以采取下列措施：</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">（一）进入现场检查；</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">（二）询问有关人员；</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">（三）要求被检查对象提供与检查事项相关的文件资料，并做出解释和说明；</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">（四）采取记录、录音、录像、照相或者复制等方式收集有关情况和资料；</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">（五）对可能被转移、隐匿或者灭失的资料等予以封存；</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">（六）聘请符合条件的会计师事务所等第三方机构和专业人员协助开展检查；</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">（七）法律、法规规定的其他措施。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二十八条　医疗保障行政部门可以依法委托符合法定条件的组织开展医疗保障行政执法工作。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第二十九条　开展医疗保障基金使用监督检查，监督检查人员不得少于2人，并且应当出示执法证件。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">医疗保障行政部门进行监督检查时，被检查对象应当予以配合，如实提供相关资料和信息，不得拒绝、阻碍检查或者谎报、瞒报。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第三十条　定点医药机构涉嫌骗取医疗保障基金支出的，在调查期间，医疗保障行政部门可以采取增加监督检查频次、加强费用监控等措施，防止损失扩大。定点医药机构拒不配合调查的，经医疗保障行政部门主要负责人批准，医疗保障行政部门可以要求医疗保障经办机构暂停医疗保障基金结算。经调查，属于骗取医疗保障基金支出的，依照本条例第四十条的规定处理；不属于骗取医疗保障基金支出的，按照规定结算。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">参保人员涉嫌骗取医疗保障基金支出且拒不配合调查的，医疗保障行政部门可以要求医疗保障经办机构暂停医疗费用联网结算。暂停联网结算期间发生的医疗费用，由参保人员全额垫付。经调查，属于骗取医疗保障基金支出的，依照本条例第四十一条的规定处理；不属于骗取医疗保障基金支出的，按照规定结算。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第三十一条　医疗保障行政部门对违反本条例的行为做出行政处罚或者行政处理决定前，应当听取当事人的陈述、申辩；做出行政处罚或者行政处理决定，应当告知当事人依法享有申请行政复议或者提起行政诉讼的权利。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第三十二条　医疗保障等行政部门、医疗保障经办机构、会计师事务所等机构及其工作人员，不得将工作中获取、知悉的被调查对象资料或者相关信息用于医疗保障基金使用监督管理以外的其他目的，不得泄露、篡改、毁损、非法向他人提供当事人的个人信息和商业秘密。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第三十三条　国务院医疗保障行政部门应当建立定点医药机构、人员等信用管理制度，根据信用评价等级分级分类监督管理，将日常监督检查结果、行政处罚结果等情况纳入全国信用信息共享平台和其他相关信息公示系统，按照国家有关规定实施惩戒。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第三十四条　医疗保障行政部门应当定期向社会公布医疗保障基金使用监督检查结果，加大对医疗保障基金使用违法案件的曝光力度，接受社会监督。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第三十五条　任何组织和个人有权对侵害医疗保障基金的违法违规行为进行举报、投诉。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">医疗保障行政部门应当畅通举报投诉渠道，依法及时处理有关举报投诉，并对举报人的信息保密。对查证属实的举报，按照国家有关规定给予举报人奖励。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第四章　法律责任</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第三十六条　医疗保障经办机构有下列情形之一的，由医疗保障行政部门责令改正，对直接负责的主管人员和其他直接责任人员依法予以处分：</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">（一）未建立健全业务、财务、安全和风险管理制度；</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">（二）未履行服务协议管理、费用监控、基金拨付、待遇审核及支付等职责；</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">（三）未定期向社会公开医疗保障基金的收入、支出、结余等情况。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第三十七条　医疗保障经办机构通过伪造、变造、隐匿、涂改、销毁医学文书、医学证明、会计凭证、电子信息等有关资料或者虚构医药服务项目等方式，骗取医疗保障基金支出的，由医疗保障行政部门责令退回，处骗取金额2倍以上5倍以下的罚款，对直接负责的主管人员和其他直接责任人员依法予以处分。</span></p><p class=\"ql-align-justify\">	第三十八条　定点医药机构有下列情形之一的，由医疗保障行政部门责令改正，并可以约谈有关负责人；造成医疗保障基金损失的，责令退回，处造成损失金额1倍以上2倍以下的罚款；拒不改正或者造成严重后果的，责令定点医药机构暂停相关责任部门6个月以上1年以下涉及医疗保障基金使用的医药服务；违反其他法律、行政法规的，由有关主管部门依法处理：</p><p class=\"ql-align-justify\">	（一）分解住院、挂床住院；</p><p class=\"ql-align-justify\">	（二）违反诊疗规范过度诊疗、过度检查、分解处方、超量开药、重复开药或者提供其他不必要的医药服务；</p><p class=\"ql-align-justify\">	（三）重复收费、超标准收费、分解项目收费；</p><p class=\"ql-align-justify\">	（四）串换药品、医用耗材、诊疗项目和服务设施；</p><p class=\"ql-align-justify\">	（五）为参保人员利用其享受医疗保障待遇的机会转卖药品，接受返还现金、实物或者获得其他非法利益提供便利；</p><p class=\"ql-align-justify\">	（六）将不属于医疗保障基金支付范围的医药费用纳入医疗保障基金结算；</p><p class=\"ql-align-justify\">	（七）造成医疗保障基金损失的其他违法行为。</p><p class=\"ql-align-justify\">	第三十九条　定点医药机构有下列情形之一的，由医疗保障行政部门责令改正，并可以约谈有关负责人；拒不改正的，处1万元以上5万元以下的罚款；违反其他法律、行政法规的，由有关主管部门依法处理：</p><p class=\"ql-align-justify\">	（一）未建立医疗保障基金使用内部管理制度，或者没有专门机构或者人员负责医疗保障基金使用管理工作；</p><p class=\"ql-align-justify\">	（二）未按照规定保管财务账目、会计凭证、处方、病历、治疗检查记录、费用明细、药品和医用耗材出入库记录等资料；</p><p class=\"ql-align-justify\">	（三）未按照规定通过医疗保障信息系统传送医疗保障基金使用有关数据；</p><p class=\"ql-align-justify\">	（四）未按照规定向医疗保障行政部门报告医疗保障基金使用监督管理所需信息；</p><p class=\"ql-align-justify\">	（五）未按照规定向社会公开医药费用、费用结构等信息；</p><p class=\"ql-align-justify\">	（六）除急诊、抢救等特殊情形外，未经参保人员或者其近亲属、监护人同意提供医疗保障基金支付范围以外的医药服务；</p><p class=\"ql-align-justify\">	（七）拒绝医疗保障等行政部门监督检查或者提供虚假情况。</p><p class=\"ql-align-justify\">	第四十条　定点医药机构通过下列方式骗取医疗保障基金支出的，由医疗保障行政部门责令退回，处骗取金额2倍以上5倍以下的罚款；责令定点医药机构暂停相关责任部门6个月以上1年以下涉及医疗保障基金使用的医药服务，直至由医疗保障经办机构解除服务协议；有执业资格的，由有关主管部门依法吊销执业资格：</p><p class=\"ql-align-justify\">	（一）诱导、协助他人冒名或者虚假就医、购药，提供虚假证明材料，或者串通他人虚开费用单据；</p><p class=\"ql-align-justify\">	（二）伪造、变造、隐匿、涂改、销毁医学文书、医学证明、会计凭证、电子信息等有关资料；</p><p class=\"ql-align-justify\">	（三）虚构医药服务项目；</p><p class=\"ql-align-justify\">	（四）其他骗取医疗保障基金支出的行为。</p><p class=\"ql-align-justify\">	定点医药机构以骗取医疗保障基金为目的，实施了本条例第三十八条规定行为之一，造成医疗保障基金损失的，按照本条规定处理。</p><p class=\"ql-align-justify\">	第四十一条　个人有下列情形之一的，由医疗保障行政部门责令改正；造成医疗保障基金损失的，责令退回；属于参保人员的，暂停其医疗费用联网结算3个月至12个月：</p><p class=\"ql-align-justify\">	（一）将本人的医疗保障凭证交由他人冒名使用；</p><p class=\"ql-align-justify\">	（二）重复享受医疗保障待遇；</p><p class=\"ql-align-justify\">	（三）利用享受医疗保障待遇的机会转卖药品，接受返还现金、实物或者获得其他非法利益。</p><p class=\"ql-align-justify\">	个人以骗取医疗保障基金为目的，实施了前款规定行为之一，造成医疗保障基金损失的；或者使用他人医疗保障凭证冒名就医、购药的；或者通过伪造、变造、隐匿、涂改、销毁医学文书、医学证明、会计凭证、电子信息等有关资料或者虚构医药服务项目等方式，骗取医疗保障基金支出的，除依照前款规定处理外，还应当由医疗保障行政部门处骗取金额2倍以上5倍以下的罚款。</p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第四十二条　医疗保障等行政部门、医疗保障经办机构、定点医药机构及其工作人员收受贿赂或者取得其他非法收入的，没收违法所得，对有关责任人员依法予以处分；违反其他法律、行政法规的，由有关主管部门依法处理。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第四十三条　定点医药机构违反本条例规定，造成医疗保障基金重大损失或者其他严重不良社会影响的，其法定代表人或者主要负责人5年内禁止从事定点医药机构管理活动，由有关部门依法予以处分。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第四十四条　违反本条例规定，侵占、挪用医疗保障基金的，由医疗保障等行政部门责令追回；有违法所得的，没收违法所得；对直接负责的主管人员和其他直接责任人员依法予以处分。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第四十五条　退回的基金退回原医疗保障基金财政专户；罚款、没收的违法所得依法上缴国库。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第四十六条　医疗保障等行政部门、医疗保障经办机构、会计师事务所等机构及其工作人员，泄露、篡改、毁损、非法向他人提供个人信息、商业秘密的，对直接负责的主管人员和其他直接责任人员依法予以处分；违反其他法律、行政法规的，由有关主管部门依法处理。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第四十七条　医疗保障等行政部门工作人员在医疗保障基金使用监督管理工作中滥用职权、玩忽职守、徇私舞弊的，依法予以处分。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第四十八条　违反本条例规定，构成违反治安管理行为的，依法给予治安管理处罚；构成犯罪的，依法追究刑事责任。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">违反本条例规定，给有关单位或者个人造成损失的，依法承担赔偿责任。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第五章　附则</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第四十九条　职工大额医疗费用补助、公务员医疗补助等医疗保障资金使用的监督管理，参照本条例执行。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">居民大病保险资金的使用按照国家有关规定执行，医疗保障行政部门应当加强监督。</span></p><p class=\"ql-align-justify\">	<span style=\"color: rgb(51, 51, 51);\">第五十条　本条例自2021年5月1日起施行。</span></p><p><br></p>','0','admin','2022-04-15 23:04:23','',NULL,NULL,''),(54,'2021年度合同制护理招聘报名人员资格审查通知','2','<p>	1、资格审查时间：2021年4月8日（周四）上午8:00-9:00</p><p>	2、资格审查地点：综合楼四楼大会议室</p><p>	3、资格审查所需材料：</p><p>	（1）《普渡健康医院人才招聘登记表》（附件1）；</p><p>	（2）毕业证书、学位证书原件及复印件；</p><p>	（3）护士执业证书原件及复印件、护士资格证书原件及复印件；</p><p>	（4）有工作单位的，必须出具二甲医院工作证明，加盖单位人事部门或单位公章；</p><p>	（5）身份证原件及复印件；</p><p>	（6）《应聘人员承诺书》（附件2）</p><p>	（7）个人简历；</p><p>	（8）两周内核酸检测结果报告。</p><p>	<strong>※特别提示：参加资格审查人员以医院电话通知为准！</strong></p>','0','admin','2022-04-15 23:05:19','',NULL,NULL,''),(55,'关于组织申报2021年度山东省自然科学基金项目（第一批）的通知','2','<p>	各科室：</p><p>	按照《山东省自然科学基金管理办法》规定，现就申报2021年度山东省自然科学基金项目（第一批）有关事项通知如下。</p><p>	一、申报条件和项目类别</p><p>	（一）申报条件</p><p>	1.申报人须为我省行政区域内高校、科研机构、企业（含中央驻鲁单位）、新型研发机构的正式在职科研人员或聘期能覆盖项目执行期的全职聘用人员。强化应用导向，鼓励支持成长型科技企业、新型研发机构科研人员申报。</p><p>	2.项目申请人仅限申报1项省自然科学基金项目（含省自然科学基金重点项目、联合基金、重大基础研究项目）。根据《省科技计划限项申报规定》，有2项及以上在研各类省科技计划项目的负责人不得申报。</p><p>	3.根据《山东省自然科学基金管理办法》第三十七、三十八、三十九条之规定，不具备申请省自然科学基金条件的不得申报。</p><p>	（二）项目类别及具体要求</p><p>	省自然基金按照“聚焦前沿，鼓励探索，需求牵引，交叉融通”的思路，坚持自愿申请、公开透明、竞争择优、公平公正原则，鼓励和支持科研人员按照突出原创（0到1）、聚焦前沿、需求牵引、交叉融通等不同的科学问题属性开展基础研究、应用开发和成果转化研究等科技创新活动。</p><p>	1.青年基金。充分发挥省自然科学基金“第一桶金”作用，支持35周岁以下青年科技人员自主选题，独立开展创新性的基础研究与应用基础研究。2020年度该类立项项目资助额度不超过15万元/项。</p><p>	具体要求：申报人年龄不超过35周岁（1986年1月1日后出生），具有从事基础研究所需的基本能力和条件。</p><p>	年龄不超过35周岁（1986年1月1日后出生），毕业于软科世界大学学术排名前200位高校、自然指数前100位高校和科研院所的博士，在《关于转发科技部等部门&lt;加强“从0到1”基础研究工作方案&gt;的通知》发布之日后（2020年8月12日）来鲁创新创业的，须提交申报书及相关证明材料。</p><p>	已获得国家自然科学基金、省自然科学基金各类项目的负责人不得申报。</p><p>	2.面上项目。支持具有一定科研基础和发展潜力的科研人员，在省自然科学基金资助范围内，瞄准学科发展前沿自主选题，开展具有前瞻性、创新性和较为深入的科学研究，促进各学科均衡、协调和可持续发展。2020年度该类立项项目资助额度不超过10万元/项。</p><p>	具体要求：申报人须具有博士学位或高级专业技术职称，具有从事基础研究课题的经历。正在主持国家自然科学基金、省自然科学基金的项目负责人不得申报。</p><p>	3.省优秀青年人才基金（简称“省优青”）。支持在基础研究方面已经取得较好成绩、发展潜力较大的38周岁以下优秀青年科研人员，为培育有望冲击省自然科学基金杰出青年基金、国家优青及其他更高层次人才做储备。2020年度该类立项项目资助额度不超过50万元/项。</p><p>	具体要求：申报人年龄不超过38周岁（1983年1月1日后出生），须具有博士学位或高级专业技术职称，具有从事基础研究课题的经历。</p><p>	年龄不超过38周岁（1983年1月1日后出生），在《关于转发科技部等部门&lt;加强“从0到1”基础研究工作方案&gt;的通知》发布之日后（2020年8月12日）申请国家优青进入最后评审阶段未获立项的申请人，须提交申请书及有关证明材料。</p><p>	长江学者、国家杰出青年科学基金获得者、国家优秀青年基金获得者、省自然科学优秀青年、杰出青年基金获得者不得申报。</p><p>	4.省自然科学杰出青年基金（简称“省杰青”）。支持40周岁以下在相关研究领域已取得突出成绩，有望获得国家杰出青年基金或国家自然科学基金重点项目等资助的优秀青年科研人员，开展高水平的基础研究与应用基础研究，培养学术骨干和学科带头人。2020年度该类立项项目资助额度不超过100万元/项。</p><p>	具体要求：申报人年龄不超过40周岁（1981年1月1日后出生），须具有博士学位或高级专业技术职称，具有从事基础研究课题的经历。</p><p>	年龄不超过40周岁（1981年1月1日后出生），在《关于转发科技部等部门&lt;加强“从0到1”基础研究工作方案&gt;的通知》发布之日后（2020年8月12日）申请国家杰青进入最后评审阶段未获立项的申请人，须提交申请书及有关证明材料。</p><p>	长江学者、国家杰出青年科学基金获得者、省自然科学杰出青年基金的获得者不得申报。省优青获得者在项目执行期的第一、二年不得申报。</p><p>	二、申报方式及时间</p><p>	1.项目申报通过山东省科技云平台（http://cloud.sdstc.gov.cn/）在线填报，使用省政府统一服务门户注册的个人账号进行网上申报。</p><p>	2.项目申请人网上申报提交时间为2021年2月18日9:00至2021年3月8日16:00，逾期无法填报及修改。</p><p>	3.单位审核提交及主管部门推荐截止时间为2021年3月10日16:00，逾期无法审核提交。</p><p>	4.请申报人于2021年3月8日前将山东省自然科学基金申报项目推荐汇总表（相关模板下载地址http://cloud.sdstc.gov.cn/bzzx/1558.jhtml，申报书模板仅供参考，以系统填报为准）<span style=\"color: rgb(30, 80, 162);\">电子版发送至科研科邮箱tyfykyk@163.com</span>。</p><p>	业务咨询电话：0531-66777204，66777035</p><p>	技术支持电话：0531-66777094</p>','0','admin','2022-04-15 23:06:45','',NULL,NULL,''),(56,'后勤零星维修工程服务单位遴选邀请函','2','<p>	根据我院2021年后勤零星维修服务要求，现以公开集中议价方式对零星维修工程服务单位进行遴选，并建立服务单位库。欢迎符合资质的维修服务单位踊跃参加。具体内容如下：</p><p>	1、服务内容：医院内的小型零星工程包括管道、上下水维修及安装，小型土建安装维修，房屋防水及房屋简单装饰、装修、维修，环境绿化，物品搬运、陪人椅与病床维修，锅炉及附属设施维修、暖气与蒸汽管道维修，电器与电路安装及维修等。</p><p>	2、资质要求：</p><p>	提供有效的营业执照，具备与本次服务项目相应的经营范围，并在人员、设备、资金等方面具有相应的服务能力。</p><p>	3、报名要求：由于医院后勤零星维修项目繁多，参与遴选的后勤零星维修单位可按照服务内容进行分类选报。</p><p>	4、会议时间：2021年1月14日14时30分</p><p>	5、会议地点：后勤管理部办公室</p><p>	6、咨询电话：0538-6236478</p><p>	7、报名电话：0538-6236439</p><p>	报名时间：2021年1月11日至1月13日（工作时间）</p>','0','admin','2022-04-15 23:07:50','',NULL,NULL,''),(57,'戴口罩！少聚集！返泰人员提前3天报备！泰安发布重要消息！','2','<p>	<span style=\"color: rgb(0, 0, 0);\">目前，全球新冠肺炎疫情仍处于高发态势，国内多地报告新增本土散发病例或聚集性疫情，加之春节临近，返乡人员大规模流动和聚集性活动显著增多，极大增加疫情传播风险，疫情防控形势依然严峻。为做好春节期间新冠肺炎疫情防控工作，营造健康祥和有序的节日生活，现发布如下温馨提示：</span></p><p>	<strong style=\"color: rgb(0, 82, 255);\">1.提前报备信息。返泰人员应提前3天向目的地所在社区（村）和单位报备</strong><span style=\"color: rgb(0, 82, 255);\">，</span>&nbsp;<span style=\"color: rgb(0, 0, 0);\">如实说明当前所在地是否属于疫情中高风险地区，个人是否进行核酸检测，有无新冠肺炎确诊病例、疑似病例或无症状感染者接触史，近期有无境外或境内疫情中高风险地区旅居史，计划返回时间、返回方式，目前健康状况等基本信息。</span></p><p>	<strong style=\"color: rgb(0, 82, 255);\">2.落实防控措施。</strong>&nbsp;<span style=\"color: rgb(0, 0, 0);\">入境返泰人员在口岸城市结束14天集中隔离医学观察后，入泰经“点对点”闭环转运、检测后，继续居家健康管理7天，满7天再做一次核酸检测；</span>&nbsp;<strong style=\"color: rgb(0, 82, 255);\">国内疫情中、高风险等疫情重点地区返泰人员，应主动报告来源地及健康状况，进行2次核酸检测（间隔24小时）、1次血清抗体检测和7天健康监测</strong><span style=\"color: rgb(0, 82, 255);\">；</span>&nbsp;<span style=\"color: rgb(0, 0, 0);\">国内疫情发生地的低风险地区返泰人员，应主动配合完成1次核酸检测。国内返乡人员在返乡后14天内应做好日常健康监测，每日自觉测量两次体温，减少串门走动和非必要外出。</span></p><p>	<strong style=\"color: rgb(0, 82, 255);\">3.合理安排出行。</strong>&nbsp;<span style=\"color: rgb(0, 0, 0);\">提倡就地过节、网络拜年，做到非必要不离泰、非必要不出境，非必要不前往中高风险地区所在城市。如必须前往，特别是跨省出行，请务必提前向所在单位或社区（村）报备，提前详细了解目的地的疫情形势，备好口罩、手消毒液等防护用品，错峰出行、预约出行。</span>&nbsp;<strong style=\"color: rgb(0, 82, 255);\">老年人、慢性病患者、孕妇等特殊人群不建议出行。</strong></p><p>	<strong style=\"color: rgb(0, 82, 255);\">4.减少人员聚集。</strong>&nbsp;<span style=\"color: rgb(0, 0, 0);\">按照“非必要不举办”原则，从严控制大型会议活动数量规模，原则上不举办各类庙会、体育赛事等聚集性活动，50人以上活动应制定疫情防控方案预案，向属地疫情防控指挥部报批并严格落实各项防控措施责任。倡导商场、超市、宾馆、酒店、企业等不举办庆典庆祝、联欢、年会等大型聚集性活动。</span>&nbsp;<strong style=\"color: rgb(0, 82, 255);\">建议市民少外出、少聚集，减少不必要的聚餐聚会，家庭私人聚会聚餐控制在10人以下。</strong></p><p>	<strong style=\"color: rgb(0, 82, 255);\">5.加强个人防护。坚持“出门必戴口罩”，尽量避免前往人群密集场所，</strong>&nbsp;<span style=\"color: rgb(0, 0, 0);\">与人交往时保持1米以上距离，提倡使用公筷公勺、分餐进食、遵守咳嗽礼仪，减少交叉感染。前往商超、餐饮单位、宾馆饭店、农贸市场、机场、火车站、长途客运站和影剧院等重点场所，严格遵守防疫规定，自觉出示健康码，配合做好体温检测、科学佩戴口罩、手消毒等防护措施。</span></p><p>	<strong style=\"color: rgb(0, 82, 255);\">6.正确使用进口产品。不购买“冷链三证”不齐全的进口冷链食品</strong><span style=\"color: rgb(0, 82, 255);\">，</span>&nbsp;<span style=\"color: rgb(0, 0, 0);\">在清洗、加工过程中，要佩戴口罩，避免用手触摸口、鼻和眼睛；操作完成后，立即做好手部清洗消毒，对可能接触到进口冷链食品的厨具、炊具及时清洗。</span>&nbsp;<strong style=\"color: rgb(0, 82, 255);\">尽量避免接触海外高风险地区的包裹、邮件、货物等</strong><span style=\"color: rgb(0, 82, 255);\">，</span>&nbsp;<span style=\"color: rgb(0, 0, 0);\">如有需要，应佩戴一次性手套、口罩，对物品及其外包装进行消毒，并在通风处放置一段时间后使用。</span></p><p>	<strong style=\"color: rgb(0, 82, 255);\">7.自觉做好健康监测。</strong>&nbsp;<span style=\"color: rgb(0, 0, 0);\">广大市民要主动做好个人及家庭成员的健康监测，出现发热、咳嗽等症状时，</span>&nbsp;<strong style=\"color: rgb(0, 82, 255);\">请在做好个人防护的前提下，第一时间到就近网格化发热门诊就诊，不要到小诊所就诊。</strong>&nbsp;<span style=\"color: rgb(0, 0, 0);\">就诊时要如实向医务人员告知外出旅行居住史及人员接触情况。发现身边亲属、邻居、同事有境外旅居史、中高风险地区旅居史的，要提醒其及时向所在社区（村）、单位报告并落实相应健康管理措施。及时关注官方权威信息，做到科学防疫，不信谣、不传谣。</span></p><p>	<span style=\"color: rgb(0, 0, 0);\">防控疫情，人人有责，从我做起，从家庭做起，从细节做起，共同筑牢常态化疫情防控防线。</span></p>','0','admin','2022-04-15 23:08:41','',NULL,NULL,''),(58,'关于申报2020年度山东省中医药科技项目的通知','2','<p>	各科室：</p><p>	2020年度山东省中医药科技项目已开始申报，现将有关事项通知如下：</p><p>	一、申报项目类别</p><p>	（一）青年项目。主要支持35岁以下青年科技人员自主选题，独立开展创新性的中医药基础研究与应用基础研究，促进青年科技人才快速成长。</p><p>	（二）面上项目。主要支持具有一定科研基础和发展潜力的科研人员，瞄准中医药学科发展前沿自主选题，开展具有前瞻性、创新性和较为深入的科学研究，促进中医药学科可持续发展。</p><p>	（三）重点项目。主要支持研究基础好、创新实力强的领军科技人才及科研团队，围绕我省中医药发展的重大需求，深入系统的开展引领性、战略性和原创性研究，推动实现前瞻性基础研究、引领性原创成果重大突破。</p><p>	二、申报条件</p><p>	（一）项目第一申请人必须是具有中级以上（含中级）专业技术职称或具有硕士及以上学位的在职人员，青年项目年龄一般不超过35周岁，面上项目年龄一般不超过45周岁，重点项目年龄一般不超过55周岁。</p><p>	（二）存在以下情况之一的，项目申请不予受理：</p><p>	1.承担省中医药科技在研项目尚未结题者；</p><p>	2.项目组成员在以往主持或参加的科研中有违背科学和伦理道德原则，有学术不端行为记录者；</p><p>	3.申请主要内容超出申报指南（附件1）以外或已获其它资助项目；</p><p>	（三）由两家以上单位联合申报的项目，需附合作协议。</p><p>	三、申报要求</p><p>	（一）各研究项目立题依据充分，有较好的前期研究基础；有明确的研究目标和合理的研究方案，研究方法、技术路线合理可行，预期成果应具有科学性、先进性和实用性，有推广应用价值。</p><p>	（二）同一项目只能申报一个类别项目，作为第一申请人只能申报一个项目；已在国家部委、省相关厅局科技计划立项的项目，不得重复申报本次中医药科技项目。</p><p>	（三）涉及实验动物的项目，必须有符合要求的医学实验动物及其设施；涉及病原微生物实验的研究项目，应提供相关生物安全实验室备案凭证复印件；涉及患者的临床研究项目，应有机构医学伦理委员会的审查意见。</p><p>	（四）本次采取限额申报方式，我院限额不超过5项。</p><p>	四、申报程序</p><p>	（一）书面申请书一式2份交至科研科，电子版和汇总表发至tyfykyk@163.com。</p><p>	（二）申报时间。自本通知发布之日起开始，截止时间为2021年1月15日17:00。</p>','0','admin','2022-04-15 23:09:29','',NULL,NULL,''),(59,'关于科学规范佩戴口罩加强个人自我防护的通告','2','<p>	科学规范佩戴口罩可以有效切断病毒传播，是预防新冠肺炎疫情的最有效手段。当前，全球新冠肺炎疫情加剧蔓延，近期国内多地报告本土散发病例或聚集性疫情，疫情防控形势依然严峻复杂。为扎实做好今冬明春疫情防控工作，现就科学规范佩戴口罩、加强个人自我防护有关事项通告如下：</p><p>	1.广大市民要强化自我防护意识，自觉遵守疫情防控相关规定，减少人员流动和聚集性活动，保持“戴口罩、勤洗手、常通风、用公筷、1米线”等良好习惯。出入以下公共场所的人员，必须佩戴一次性使用医用口罩或医用外科口罩：</p><p>	（1）医疗机构就诊、探视或陪护人员；</p><p>	（2）水产品、冷冻食品和农副产品批发市场、农贸市场等场所工作人员及顾客；</p><p>	（3）公交车、出租车、网约车、长途汽车、火车、飞机等公共交通工具的司乘人员；</p><p>	（4）学校进出人员，值守人员、清洁人员及食堂工作人员；</p><p>	（5）商场、超市、购物中心、药店及酒店、宾馆等公共服务场所工作人员及顾客；</p><p>	（6）银行、展览馆、图书馆、博物馆、美术馆及各类办事大厅、车站等场所的工作人员和出入人员；</p><p>	（7）理发店、美容院、影剧院、游艺厅、网吧、体育场馆（运动时除外）、歌舞厅等密闭场所的工作人员及顾客；</p><p>	（8）养老院、敬老院、福利院提供服务的工作人员和外来人员；</p><p>	（9）在密闭空间参加会议、培训人员；</p><p>	（10）电梯等其他通风不良或人员密集场所活动的人员及按照行业管理规范要求必须佩戴口罩的人员。</p><p>	2.各公共场所单位（经营主体）要落实疫情防控主体责任，在场所入口处设置醒目、清晰的佩戴口罩提示，加强进入场所人员佩戴口罩等管理工作，对未佩戴口罩者禁止入内。对不听劝阻、扰乱秩序者，由公安机关依法予以处理。</p><p>	3.各县市区、功能区和各有关部门要严格落实属地管理、行业监管责任，加强对重点场所佩戴口罩等防控措施落实情况的监督检查，及时发现并督促整改问题。</p><p>	</p>','0','admin','2022-04-15 23:09:59','',NULL,NULL,''),(60,'关于申报2020年度论文、著作、专利、课题和获奖成果奖励的通知','2','<p>	各科室：</p><p>	根据山一大二附院发[2020]125号文，2020年度论文、著作、专利、课题和获奖成果奖励开始申报，现将申报相关事项通知如下：</p><p>	一、申报内容</p><p>	2020年度我院职工获省部级以上的政府部门科研奖励，以及山东医学奖、山东省高等学校优秀科研成果奖、泰安市科技进步奖等同等级别奖励的成果；公开发表被SCI、SCIE收录的论文，以及在北图核心期刊发表的论文（不包括简讯、摘要、书评、个案报道、病例讨论、译文、讲座、会议纪要及在增刊、专刊、论文集上发表的论文）；出版的著作；授权的职务发明（专利权人为我院）专利（包括发明专利、实用新型、外观设计、软件著作权）；2020年立项的上级科研课题，均可申报科研奖励。</p><p>	二、报送材料</p><p>	1．申报明细表、统计表纸质版各1份（在<span style=\"color: rgb(30, 80, 162);\">tyfykyk@163.com</span>或医院网站下载），电子版发送至<span style=\"color: rgb(30, 80, 162);\">tyfykyk@163.com</span>。</p><p>	2.申报专利奖励、SCI、SSCI收录论文奖励者需提供专利证书和论文收录证明的复印件1份备案。专利证书PDF版、SCI、SSCI收录论文的PDF版发送至<span style=\"color: rgb(30, 80, 162);\">tyfykyk@163.com</span>。著作以及在北图核心期刊发表的论文，需提供原件。</p><p>	三、时间安排</p><p>	申报者在2021年1月15日下午下班前将申报材料报送科研科，逾期不予受理。</p>','0','admin','2022-04-15 23:10:52','',NULL,NULL,''),(61,'我院成为九江市人民医院、中国科学院大学九江医院的健康管理专科联盟理事单位。','3',NULL,'0','admin','2021-08-01 23:30:13','',NULL,NULL,''),(62,'我院成功创建市级博士后科研工作站。','3',NULL,'0','admin','2020-07-15 23:30:33','',NULL,NULL,''),(63,'我院成为北京积水潭骨科研究联盟成员。','3',NULL,'0','admin','2020-06-18 23:31:37','',NULL,NULL,''),(64,'我院顺利通过三级甲等中医医院复评验收。','3',NULL,'0','admin','2019-05-13 23:32:13','',NULL,NULL,''),(65,'我院被九江市卫生局授予”十佳医院“称号。','3',NULL,'0','admin','2015-06-11 23:33:29','',NULL,NULL,''),(66,'我院被中华人民共和国卫生部、联合国儿童基金会、世界卫生组织授予“爱婴医院”荣誉称号。','3',NULL,'0','admin','2012-06-14 23:34:16','',NULL,NULL,''),(67,'我院荣获“全国文明服务示范医院”称号。','3',NULL,'0','admin','2010-02-03 23:35:01','',NULL,NULL,''),(68,'普渡健康医院在九江市正式成立','3',NULL,'0','admin','2005-08-12 23:35:41','',NULL,NULL,'');

/*Table structure for table `sys_oper_log` */

DROP TABLE IF EXISTS `sys_oper_log`;

CREATE TABLE `sys_oper_log` (
  `oper_id` bigint NOT NULL AUTO_INCREMENT COMMENT '日志主键',
  `title` varchar(50) DEFAULT '' COMMENT '模块标题',
  `business_type` int DEFAULT '0' COMMENT '业务类型（0其它 1新增 2修改 3删除）',
  `method` varchar(100) DEFAULT '' COMMENT '方法名称',
  `request_method` varchar(10) DEFAULT '' COMMENT '请求方式',
  `operator_type` int DEFAULT '0' COMMENT '操作类别（0其它 1后台用户 2手机端用户）',
  `oper_name` varchar(50) DEFAULT '' COMMENT '操作人员',
  `dept_name` varchar(50) DEFAULT '' COMMENT '部门名称',
  `oper_url` varchar(255) DEFAULT '' COMMENT '请求URL',
  `oper_ip` varchar(128) DEFAULT '' COMMENT '主机地址',
  `oper_location` varchar(255) DEFAULT '' COMMENT '操作地点',
  `oper_param` varchar(2000) DEFAULT '' COMMENT '请求参数',
  `json_result` varchar(2000) DEFAULT '' COMMENT '返回参数',
  `status` int DEFAULT '0' COMMENT '操作状态（0正常 1异常）',
  `error_msg` varchar(2000) DEFAULT '' COMMENT '错误消息',
  `oper_time` datetime DEFAULT NULL COMMENT '操作时间',
  PRIMARY KEY (`oper_id`)
) ENGINE=InnoDB AUTO_INCREMENT=386 DEFAULT CHARSET=utf8 COMMENT='操作日志记录';

